INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6512,'math',NULL,'w(t) = y(t-t_0)',9,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6513,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: Integrator is Time-Invariant</span><p>% The integrator system is also a time-invariant system.  To prove this, we replace <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x(\\tau)"/> in <a href="eq:integrator" class="ITS_reference">&raquo;</a> by <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x(\\tau-t_0)"/> obtaining the output <span class="ITS_HTML">w(t)</span> <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?w(t) = \\int_{-\\infty}^t x(\\tau-t_0)d\\tau"/></div> Now, to prove time-invariance, we must manipulate the integral in <a href="eq:integrator1" class="ITS_reference">&raquo;</a>  into a form that is recognizable in terms of the original output <span class="ITS_HTML">y(t)</span>.  This is done by changing the &ldquo;dummy variable&rdquo; of  integration  to <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\sigma=\\tau-t_0"/>.  In this substitution, <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?d\\tau"/> is replaced by <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?d\\sigma"/>, the lower limit <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tau=-\\infty"/> becomes <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\sigma=-\\infty"/>,  and the upper limit <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tau=t"/> becomes <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\sigma=t-t_0"/>.  Therefore <a href="eq:integrator1" class="ITS_reference">&raquo;</a> becomes <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? w(t) = \\int_{-\\infty}^{t-t_0} x(\\sigma)d\\sigma "/></div> and it is now clear that <span class="ITS_HTML">w(t) = y(t-t<sub>0</sub>)</span>, so the integrator system is seen to be  time-invariant. </div>','9','5','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6514,'equation','eq:amp_mod','y(t)= x(t)\\cos(\\omega_c t)',9,5,6);
