INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6800,'paragraph',NULL,'Now we are ready to draw the sketch that shows how <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?h(\\tau)"/> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x(t-\\tau)"/> interact in evaluating the convolution integral <a href="eq:conv_int" class="ITS_reference">&raquo;</a>.  Figure <a href="fig:eval_conv" class="ITS_reference">&raquo;</a>(a) shows  a plot of both <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?h(\\tau)"/> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x(t-\\tau)"/> on the same set of axes for two different choices of <span class="ITS_HTML">t</span>. \figurepsAltScale{0.9}{Chapter09/art/eval_conv} {Illustration of evaluation of convolution. (a) the signal <span class="ITS_HTML">h(t)</span> and flipped versions of <span class="ITS_HTML">x(t)</span> plotted on the <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tau"/> axis, (b) the output signal <span class="ITS_HTML">y(t)</span> plotted versus <span class="ITS_HTML">t</span>. The gray axis indicates a change from <span class="ITS_HTML">t</span> to <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tau"/>.   } Note that the graph <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?h(\\tau)"/> is identical to the graph <span class="ITS_HTML">h(t)</span> in Fig. <a href="fig:x_and_h" class="ITS_reference">&raquo;</a>(a) only with its axis relabeled <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tau"/> instead of <span class="ITS_HTML">t</span>.  Also shown in Fig. <a href="fig:eval_conv" class="ITS_reference">&raquo;</a> is <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x(t-\\tau)=u(t-\\tau-1)"/> for two different values of <span class="ITS_HTML">t</span>.','9','8','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6801,'equation','none','\\begin{align*} y(t) &= \\int_0^{t-1} e^{-\\tau}d\\tau \\\\ &=  \\left. - e^{-\\tau}\\;\\right]_0^{t-1} \\\\ &=  - e^{-(t-1)}+1 \\\\ &=  1-e^{-(t-1)} \\quad  \\text{for $t>1$} \\end{align*}',9,8,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6802,'equation','eq:conv_example_1','y(t)= (1-e^{-(t-1)})u(t-1)',9,8,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6803,'equation','none',' y(t) =0 \\quad   \\mbox{for $t<1$} ',9,8,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6804,'equation','none',' y(t) = \\int_0^{t-1} e^{-\\tau}d\\tau \\quad   \\mbox{for $t>1$} ',9,8,7);
