INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6865,'math',NULL,'h(t)',9,8,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6866,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Show by drawing pictures like Fig. <a href="fig:eval_conv" class="ITS_reference">&raquo;</a>(a) that if <span class="ITS_HTML">x(t)=0</span> for <span class="ITS_HTML">t<T<sub>1</sub></span> and <span class="ITS_HTML">h(t)=0</span> for <span class="ITS_HTML">t<T<sub>2</sub></span>, then the output <span class="ITS_HTML">y(t)=x(t)*h(t)</span> is zero for <span class="ITS_HTML">t<T<sub>1</sub>+T<sub>2</sub></span>. In other words, the starting time of <span class="ITS_HTML">y(t)</span> is the sum of the starting times of <span class="ITS_HTML">x(t)</span> and <span class="ITS_HTML">h(t)</span>. </div>','9','8','12');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6867,'equation','none',' y(t)=\\int_{-\\infty}^{\\infty} x(\\tau)h(t-\\tau)d\\tau ',9,8,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6868,'math',NULL,'x(t)=u(t-1)',9,8,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6869,'math',NULL,'h(t)=e^{-t}u(t)',9,8,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6870,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Repeat the convolution of <span class="ITS_HTML">x(t)=u(t-1)</span> with <span class="ITS_HTML">h(t)=e<sup>-t</sup>u(t)</span>, but this time use the convolution integral form <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? y(t)=\\int_{-\\infty}^{\\infty} x(\\tau)h(t-\\tau)d\\tau "/></div> Because convolution is commutative, your answer should be the same as <a href="eq:conv_example_1" class="ITS_reference">&raquo;</a>. </div>','9','8','14');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6871,'paragraph',NULL,'In order to provide a tool for studying convolution for continuous-time systems, a MATLAB  GUI was created to run different cases where the signals can be selected from a menu of pulses, exponentials, sinusoids and impulses. The screen shot of Fig. <a href="fig:cconvdemoGUI" class="ITS_reference">&raquo;</a> shows the GUI with a pulse convolved with an exponential. The output signal is shown in the bottom panel of the GUI. <h3 class="ITS_NOTE"><b>DEMO:</b> Continuous Convolution</h3>','9','8','17');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6872,'math',NULL,'x(t) = u(t-1)-u(t-3)',9,8,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6873,'math',NULL,'h(t) = e^{-0.3t}[u(t)-u(t-8)]',9,8,18);
