INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6875,'math',NULL,'t=8',9,8,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6876,'image','Chapter09/art/cconvdemoGUI','Graphical User Interface (GUI) for <tt>cconvdemo</tt> which illustrates the <i> flip and slide</i> nature of continuous-time convolution. The user can  pick the two signals, and then use the mouse pointer to slide one of the signals (flipped) over the other one. The area of the overlapped signals is shown in the middle panel. The specific example shown here is <span class="ITS_HTML">x(t) = u(t-1)-u(t-3)</span> convolved with <span class="ITS_HTML">h(t) = e<sup>-0.3t</sup>[u(t)-u(t-8)]</span>.  For the specific flipped <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?h(t-\\tau)"/>, the value of the output time parameter is <span class="ITS_HTML">t=8</span>.',9,8,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6877,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch09/Fig09-01_cconvdemoGUI.png" alt="FILES/SP1Figures/Ch09/Fig09-01_cconvdemoGUI.png"></center><br><div class="ITS_Caption">Graphical User Interface (GUI) for <tt>cconvdemo</tt> which illustrates the <i> flip and slide</i> nature of continuous-time convolution. The user can  pick the two signals, and then use the mouse pointer to slide one of the signals (flipped) over the other one. The area of the overlapped signals is shown in the middle panel. The specific example shown here is <span class="ITS_HTML">x(t) = u(t-1)-u(t-3)</span> convolved with <span class="ITS_HTML">h(t) = e<sup>-0.3t</sup>[u(t)-u(t-8)]</span>.  For the specific flipped <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?h(t-\\tau)"/>, the value of the output time parameter is <span class="ITS_HTML">t=8</span>.</span></div></div>','9','8','18');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6878,'paragraph',NULL,'To become skilled at evaluating convolution integrals there is no substitute for practice and studying worked examples. Section <a href="sec:square-pulse-input" class="ITS_reference">&raquo;</a> presents a more complicated example, but first, we shall return to the discrete time-domain and show how what we have just learned can be used in the closed-form evaluation of discrete convolutions.','9','8','21');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6879,'subsection',NULL,'Evaluation of Discrete Convolution',9,8,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6880,'subsection',NULL,'Evaluation of Discrete Convolution',9,8,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6881,'paragraph',NULL,'<h3 class="ITS_BOOK">9-6.3  Evaluation of Discrete Convolution</h3>','9','8','22');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6882,'equation','eq:disc_conv_revisited','y[n]=\\sum_{k=-\\infty}^{\\infty} h[k]x[n-k] \\quad  -\\infty < n <\\infty',9,8,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6883,'math',NULL,'\\tau',9,8,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6884,'math',NULL,'k',9,8,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6885,'math',NULL,'t',9,8,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6886,'math',NULL,'n',9,8,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6887,'paragraph',NULL,'Recall that the discrete convolution sum is defined as <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?y[n]=\\sum_{k=-\\infty}^{\\infty} h[k]x[n-k] \\quad  -\\infty < n <\\infty"/></div> If we compare <a href="eq:disc_conv_revisited" class="ITS_reference">&raquo;</a> to <a href="eq:conv_int" class="ITS_reference">&raquo;</a> we see that the equations look very similar.  The integral in <a href="eq:conv_int" class="ITS_reference">&raquo;</a> becomes  a discrete sum in <a href="eq:disc_conv_revisited" class="ITS_reference">&raquo;</a>; the dummy variable of integration <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tau"/> is replaced by the dummy index of summation <span class="ITS_HTML">k</span>;  and the independent time variable of the output signal <span class="ITS_HTML">t</span> is replaced by the discrete index <span class="ITS_HTML">n</span>.  Although these are significant differences, the basic structure is the same.','9','8','23');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6888,'equation','eq:disc_conv_example','y[n]=\\sum_{k=-\\infty}^{\\infty} a^ku[k]u[n-k-1]',9,8,24);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6889,'equation','eq:closed_form_output','y[n]= \\left(\\frac{1-a^n}{1-a}\\right)u[n-1]',9,8,24);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6890,'equation','none',' y[n]=0\\quad   \\text{for } n<1 ',9,8,24);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6891,'equation','none',' y[n] = \\sum_{k=0}^{n-1} a^k = \\frac{1-a^n}{1-a}. ',9,8,24);
