INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6964,'paragraph',NULL,'\iffalse <div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Draw the plots of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x(\\tau)"/> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?h(t-\\tau)"/> as functions of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tau"/> similar to Fig. <a href="fig:eval_conv3" class="ITS_reference">&raquo;</a>(a,b) for the evaluation of the convolution of the very narrow input pulse <a href="eq:very_narrow" class="ITS_reference">&raquo;</a> with the impulse response <span class="ITS_HTML">h(t)=e<sup>-t</sup>u(t)</span>. </div> \fi','9','8','30');
