INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7069,'paragraph',NULL,'Figure <a href="fig:parallel_form" class="ITS_reference">&raquo;</a> shows a parallel connection of two LTI systems, where the same signal <span class="ITS_HTML">x(t)</span> is the input to both systems, and whose outputs are then added to produce the overall output <span class="ITS_HTML">y(t)</span>.  As in Fig. <a href="fig:cascade_form" class="ITS_reference">&raquo;</a>, the upper signal variables denote general inputs and outputs, while below the arrows are the output signals that result when the input is the unit impulse <span class="ITS_HTML">&delta;(t)</span>. \figurepsAltScale{\SCALE}{Chapter09/art/parallel_3LTI_ht}% {Parallel connection of LTI systems: (a) Parallel connection of two systems. (b)  Equivalent system to  (a). } \iffalse \begin{figure}[htb]  \thicklines \setlength{\unitlength}{.6456in} \begin{picture}(4,4.6) {\small \put(0,3.2){\line(1,0){1}} \put(1,2.6){\line(0,1){1.2}} \put(1,3.8){\vector(1,0){.6}} \put(1,2.6){\vector(1,0){.6}} \put(1.6,3.4){\framebox(1,.8){\shortstack{LTI\\ System #1 \\<span class="ITS_HTML">h<sub>1</sub>(t)</span>}}} \put(1.6,2.2){\framebox(1,.8){\shortstack{LTI\\ System #2\\<span class="ITS_HTML">h<sub>2</sub>(t)</span>}}} \put(2.6,3.8){\line(1,0){.8}} \put(2.6,2.6){\line(1,0){.8}} \put(3.4,3.8){\vector(0,-1){.5}} \put(3.4,2.6){\vector(0,1){.5}} \put(3.4,3.2){\makebox(0,0){\huge <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\oplus"/>}} \put(3.5,3.2){\vector(1,0){.9}} \put(.8,2){\dashbox{.05}(2.8,2.4)} \put(2.1,1.7){\makebox(0,0){(a)}} \put(0,3.3){\makebox(0,0)[bl]{<span class="ITS_HTML">x(t)</span>}} \put(0,3.1){\makebox(0,0)[tl]{<span class="ITS_HTML">&delta;(t)</span>}} \put(3,3.9){\makebox(0,0)[bc]{<span class="ITS_HTML">y<sub>1</sub>(t)</span>}} \put(3,3.7){\makebox(0,0)[tc]{<span class="ITS_HTML">h<sub>1</sub>(t)</span>}} \put(3,2.7){\makebox(0,0)[bc]{<span class="ITS_HTML">y<sub>2</sub>(t)</span>}} \put(3,2.5){\makebox(0,0)[tc]{<span class="ITS_HTML">h<sub>2</sub>(t)</span>}} \put(4.4,3.3){\makebox(0,0)[bc]{<span class="ITS_HTML">y(t)</span>}} \put(4.4,3.1){\makebox(0,0)[tc]{<span class="ITS_HTML">h<sub>1</sub>(t)+h<sub>2</sub>(t)</span>}} \put(3.3,3.5){\makebox(0,0)[tc]{<span class="ITS_HTML">+</span>}} \put(3.3,2.9){\makebox(0,0)[bc]{<span class="ITS_HTML">+</span>}} \put(.4,1){\vector(1,0){1}} \put(1.4,.6){\dashbox{.05}(1.4,.8){\shortstack{Equivalent \\ LTI System  \\<span class="ITS_HTML">h<sub>1</sub>(t)+h<sub>2</sub>(t)</span>}}} \put(2.8,1){\vector(1,0){1}} \put(2.1,.3){\makebox(0,0){(b)}} \put(.4,1.1){\makebox(0,0)[bl]{<span class="ITS_HTML">x(t)</span>}} \put(.4,.9){\makebox(0,0)[tl]{<span class="ITS_HTML">&delta;(t)</span>}} \put(3.8,1.1){\makebox(0,0)[bc]{<span class="ITS_HTML">y(t)</span>}} \put(3.8,.9){\makebox(0,0)[tc]{<span class="ITS_HTML">h<sub>1</sub>(t)+h<sub>2</sub>(t)</span>}} } \end{picture}   Parallel connection of LTI systems: (a) Parallel connection of two systems. (b)  Equivalent system to  (a). \end{figure} \fi','9','9','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7070,'equation','none',' h(t) = u(t-1)-u(t-2) =\\left\\{\\begin{array}{ll} 1 & \\quad  1\\leq t<2 \\\\ 0 & \\quad  \\mbox{otherwise} \\end{array} \\right. ',9,9,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7071,'equation','none',' h(t) = u(t)*[\\delta(t-1) -\\delta(t-2)] ',9,9,4);
