INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7073,'math',NULL,'h(t)',9,9,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7074,'paragraph',NULL,'Note that the overall impulse response is <span class="ITS_HTML">h<sub>1</sub>(t)+h<sub>2</sub>(t)</span>, and the parallel connection of two systems can be replaced by the equivalent system in Fig. <a href="fig:parallel_form" class="ITS_reference">&raquo;</a>(b). <div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: Parallel Connection</span><p>%Suppose that the impulse response of an LTI system is <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? h(t) = u(t-1)-u(t-2) =\\left\\{\\begin{array}{ll} 1 & \\quad  1\\leq t<2 \\\\ 0 & \\quad  \otherwise \\end{array} \\right. "/></div> Using  properties of convolution, we can express <span class="ITS_HTML">h(t)</span> as <div class="ITS_EQUATION"> h(t) = u(t)*[&delta;(t-1) -&delta;(t-2)] </div> so that the system could be implemented by a cascade of an integrator followed by a parallel combination of two delay systems. </div> <div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Draw a block diagram representation of the system in Example <a href="ex:cascade_parallel" class="ITS_reference">&raquo;</a> as an integrator followed by a parallel combination of two delay systems. </div>','9','9','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7075,'equation','none','\\begin{align*} y_1(t) = \\frac{dy(t)}{dt}  &= y(t)*\\delta^{(1)}(t) \\\\ &=  [x(t)*h(t)]*\\delta^{(1)}(t) \\\\ &=  x(t)*[h(t)*\\delta^{(1)}(t)]  \\\\ & =  x(t)*h^{(1)}(t) \\end{align*}',9,9,5);
