INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7332,'subsection',NULL,'Plotting the Frequency Response',10,2,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7333,'subsection',NULL,'Plotting the Frequency Response',10,2,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7334,'paragraph',NULL,'<h3 class="ITS_BOOK">10-0.1  Plotting the Frequency Response</h3>  The frequency response is a complex-valued function <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(j\\omega)"/> that depends on <span class="ITS_HTML">&omega;</span> and is best summarized with two plots: magnitude versus frequency and phase versus frequency. Just as in  the case of digital filters, we can talk about passbands and stopbands of analog filters once we are looking at a plot of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(j\\omega)"/>.','10','2','10');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7335,'table','','\\begin{tabular}{|c||||c|} \\hline \\omega & |H(j\\omega)|\\\\  \\hline\\hline\\hline\\hline 0 & 1 \\\\ \\hline 2 & 2/\\sqrt{8} = 0.707 \\\\ \\hline 20 & 2/\\sqrt{404} \\approx0.0995 \\\\ \\hline \\infty & 0 \\\\ \\hline \\end{tabular}',10,2,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7336,'equation','eq:filter_example_mag','\\begin{align} | H(j\\omega)| &=  \\left | \\frac{2}{2 + j\\omega} \\right | = \\frac{2}{\\sqrt{4 + \\omega^2}}  \\\\ \\angle H(j\\omega) &= 0 -\\angle \\{2 + j\\omega\\} = -\\tan^{-1} \\left( \\omega/2 \\right) LABEL#eq:filter_example_phase#LABEL \\end{align}',10,2,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7337,'table','tab:Hojomega','\renewcommand{\arraystretch}{1.25} <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{tabular}{|c||||c|} \\hline \\omega & |H(j\\omega)|\\\\  \\hline\\hline\\hline\\hline 0 & 1 \\\\ \\hline 2 & 2/\\sqrt{8} = 0.707 \\\\ \\hline 20 & 2/\\sqrt{404} \\approx0.0995 \\\\ \\hline \\infty & 0 \\\\ \\hline \\end{tabular}"/></div>',10,2,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7338,'equation','eq:exp_freq_resp','H(j\\omega) = \\frac{2}{2 + j\\omega}',10,2,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7339,'math',NULL,'H(j\\omega)',10,2,11);
