INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7364,'equation','none','\\begin{align*}  H(j200\\pi) &= \\frac{j200\\pi}{200\\pi + j200\\pi}\\\\ &= \\frac{j}{1 + j}  = \\frac{1{\\sqrt{2}} e^{+j\\pi/4}} \\end{align*}',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7365,'equation','none','\\begin{align*} y(t) &=  \\frac{1{\\sqrt{2}} e^{+j\\pi/4}} \\left( 20 e^{j0.3\\pi} e^{j200\\pi t} \\right)\\\\ &=10\\sqrt{2} e^{j(200\\pi t +0.55\\pi)} \\end{align*}',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7366,'equation','eq:CT-complex-exp_0','x(t) = Ae^{j\\phi} e^{j\\omega_0 t}',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7367,'equation','eq:sinusoidal-response','y(t) = H(j\\omega_0) Ae^{j\\phi} e^{j\\omega_0 t}',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7368,'equation','eq:output-complex-amplitude','H(j\\omega_0) = M e^{ j \\psi}',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7369,'equation','eq:sinusoidal-response-2','y(t) = M e^{ j \\psi} A e^{j\\phi} e^{ j\\omega_0 t} = ({ M} A)  e^{ j (\\omega_0 t + \\phi + { \\psi})}',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7370,'equation','none','  H(j\\omega) = \\frac{j\\omega}{200\\pi + j\\omega} ',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7371,'equation','none',' x(t) = 20 e^{j0.3\\pi} e^{j200\\pi t} ',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7372,'math',NULL,'\\omega_0',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7373,'math',NULL,'H(j\\omega)',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7374,'math',NULL,'\\omega = \\omega_0',10,2,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7375,'math',NULL,'H(j\\omega)',10,2,16);
