INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7391,'image','Chapter10/art/LTIsystemHw','0.8in} \begin{picture}(4,1.2) \put(0,.6){\vector(1,0){1}} \put(1,.2){\framebox(1.2,.8){\shortstack{LTI \\ System\\ $h(t)$, $\Homa$}}} \put(2.2,.6){\vector(1,0){1}} \put(0,.7){\makebox(0,0)[bl]{$x(t)$}} \put(0,.5){\makebox(0,0)[tl]{$\delta(t)$}} \put(0,.25){\makebox(0,0)[tl]{$Ae^{j\phi}e^{j\oma t}$}} \put(3,.7){\makebox(0,0)[bl]{$y(t)=h(t)*x(t)$}} \put(3,.5){\makebox(0,0)[tl]{$h(t)$}} \put(3,.25){\makebox(0,0)[tl]{$(\Homa Ae^{j\phi})e^{j\oma t}$}} \end{picture} \vspace*{-.2in}  \caption{Linear time-invariant system with responses to unit impulse and to complex-exponential signals.',10,3,2);
