INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7501,'equation','eq:finish-periodic-proof','\\begin{align} y(t+T_0) &= \\int\\limits_{-\\infty}^{\\infty} h(\\tau) x(t+T_0-\\tau) \\,d\\tau \\nonumber \\\\ &= \\int\\limits_{-\\infty}^{\\infty} h(\\tau) x(t-\\tau) \\,d\\tau = y(t)  \\end{align}',10,3,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7502,'equation','eq:define-periodic','x(t) = x(t + T_0) \\quad   -\\infty\\;<\\;t\\;<\\;\\infty',10,3,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7503,'equation','eq:y(t)-shifted','y(t+T_0) = \\int\\limits_{-\\infty}^{\\infty} h(\\tau) x(t+T_0-\\tau) \\,d\\tau',10,3,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7504,'equation','eq:inside-integral-periodic','x(t+T_0-\\tau) =  x(t-\\tau) \\quad  -\\infty\\;<\\;t\\;<\\;\\infty',10,3,25);
