INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7506,'math',NULL,'T_0',10,3,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7507,'math',NULL,'y(t+T_0)',10,3,25);
