INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7595,'math',NULL,'|\\omega| = \\infty',10,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7596,'math',NULL,'\\omega_\\mbox{ co}',10,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7597,'math',NULL,'\\omega_\\mbox{ co}',10,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7598,'math',NULL,'H_\\mbox{ hp}(-j\\omega)=H_\\mbox{ hp}^*(j\\omega)',10,4,12);
