INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7898,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Confirm that <a href="eq:exp_sig_pair-2" class="ITS_reference">&raquo;</a> is a valid Fourier transform pair even when <span class="ITS_HTML">a</span> is a complex number, as long as <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\mathb{R}\\{a\\}>0"/>. </div>','11','5','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7899,'equation','none','\\begin{align*} X(j\\omega) &= \\frac{1}{a+j\\omega}=\\frac{1}{a+j\\omega}\\left(\\frac{a-j\\omega}{a-j\\omega}\\right)\\\\ &= \\frac{a}{a^2+\\omega^2}+\\frac{-j\\omega}{a^2+\\omega^2} \\end{align*}',11,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7900,'equation','none','\\begin{align*} \\Re e\\{X(j\\omega)\\} &=\\frac{a}{a^2+\\omega^2}\\\\ \\Im m\\{X(j\\omega)\\} &=-\\frac{\\omega}{a^2+\\omega^2} \\end{align*}',11,5,7);
