INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7925,'subsubsection',NULL,'Bandwidth and Decay Rate',11,5,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7926,'subsubsection',NULL,'Bandwidth and Decay Rate',11,5,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7927,'paragraph',NULL,'<h3 class="ITS_BOOK">11-3.3.1  Bandwidth and Decay Rate</h3>  Figure <a href="fig:right_exp" class="ITS_reference">&raquo;</a> illustrates  a fundamental property of Fourier transform  representations&mdash;the inverse relation between time and frequency.  First of all, notice that the parameter <span class="ITS_HTML">a</span> controls the rate of decay of the exponential in the time-domain. As <span class="ITS_HTML">a</span> increases, the exponential dies out more quickly, making <span class="ITS_HTML">x(t)</span> more concentrated near <span class="ITS_HTML">t=0</span>.  In the frequency-domain, on the other hand, as <span class="ITS_HTML">a</span> increases, the Fourier transform spreads out. <div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Make  sketches or MATLAB  plots like Fig. <a href="fig:right_exp" class="ITS_reference">&raquo;</a> for <span class="ITS_HTML">a=1</span> and <span class="ITS_HTML">a=2</span>. </div>','11','5','11');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7928,'paragraph',NULL,'The inverse relationship between the time-domain and frequency-domain occurs consistently. Signals that are short in time duration are spread out in frequency; i.e., they have a wide &ldquo;bandwidth&rdquo;. Conversely, signals that are spread out in time are concentrated in frequency. A formal statement of this property is the scaling property presented in Section <a href="sec:scaling-property" class="ITS_reference">&raquo;</a> on p. <a href="sec:scaling-property" class="ITS_reference">&raquo;</a>.','11','5','12');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7929,'equation','eq:pulse_trans','\\begin{align} X(j\\omega)&= \\int_{-\\Tph}^{\\Tph} e^{-j\\omega t} dt \\nonumber \\\\ &=  \\left.\\frac{e^{-j\\omega t}}{-j\\omega}\\right|_{-\\Tph}^{\\Tph} = \\frac{e^{-j\\omega \\Tph}-e^{j\\omega \\Tph}}{-j\\omega} \\nonumber \\\\ &= \\frac{\\sin(\\omega \\Tph)}{\\omega/2}  \\end{align}',11,5,13);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7930,'equation','eq:square-pulse','x(t)%%%%% = [u(t+\\hTph)-u(t-\\hTph)] = \\begin{cases} 1 & \\quad  -\\hTph \\leq t < \\hTph \\\\ 0 & \\quad  \\mbox{otherwise} \\end{cases}',11,5,13);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(7931,'equation','eq:sq_pulse_pair',' \\begin{array}{ccc} \\BlackTD && \\OrangeFD\\\\ \\left[u(t+\\hTph) - u(t-\\hTph)\\right] & \\Fgoes & \\OrangeFT { \\frac{\\sin(\\omega \\Tph)}{\\omega/2}}  \\\\ \\end{array} ',11,5,13);
