INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8021,'math',NULL,'t',11,5,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8022,'math',NULL,'\\omega=0',11,5,26);
