INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8052,'math',NULL,'x(t)=\\sin(\\omega_0 t ) = \\cos(\\omega_0 t -\\pi/2)',11,5,29);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8053,'math',NULL,'X(j\\omega)=-j\\pi\\delta(\\omega-\\omega_0)+j\\pi\\delta(\\omega+\\omega_0)',11,5,29);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8054,'paragraph',NULL,'Figure <a href="fig:cosine" class="ITS_reference">&raquo;</a> shows the case of a sine function and its Fourier transform, which is  the special case <span class="ITS_HTML">A=1</span> and <span class="ITS_HTML">&phi;=-&pi;/2</span>. Note that the  size (area) of the impulse at negative frequency <span class="ITS_HTML">-&omega;<sub>0</sub></span> is the complex conjugate of the size of the impulse at the positive frequency. We will see that complex conjugate symmetry between the positive and negative frequency components is a general property of the Fourier transform of a <span class="ITS_blue">real</span> signal. <div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch11/Fig11-06_sine_FT.png" alt="FILES/SP1Figures/Ch11/Fig11-06_sine_FT.png"></center><br><div class="ITS_Caption">Fourier transform of a sinusoid: (a) Time function <span class="ITS_HTML">x(t)=sin(&omega;<sub>0</sub> t ) = cos(&omega;<sub>0</sub> t -&pi;/2)</span>, and (b) corresponding Fourier transform <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(j\\omega)=-j\\pi\\delta(\\omega-\\omega_0)+j\\pi\\delta(\\omega+\\omega_0)"/>.</span></div></div>','11','5','29');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8055,'paragraph',NULL,'At this point, we comment that a sine wave is one example of a signal that has a Fourier transform, even though the existence condition <a href="eq:abs_integrable" class="ITS_reference">&raquo;</a> is violated. As stated before, <a href="eq:abs_integrable" class="ITS_reference">&raquo;</a> is a sufficient condition, but not a necessary condition for having a Fourier transform.','11','5','31');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8056,'subsection',NULL,'Periodic Signals',11,5,32);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8057,'subsection',NULL,'Periodic Signals',11,5,32);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8058,'paragraph',NULL,'<h3 class="ITS_BOOK">11-3.11  Periodic Signals</h3>','11','5','32');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8059,'math',NULL,'X(j\\omega)',11,5,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8060,'math',NULL,'\\omega = 2\\pi k/T_0',11,5,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8061,'image','Chapter11/art/sq_wave_trans','Fourier transform of the square wave shown in Fig. <a href="fig:sq_wave_again" class="ITS_reference">&raquo;</a>. The transform <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(j\\omega)"/> has regularly spaced impulses at <span class="ITS_HTML">&omega; = 2&pi; k/T<sub>0</sub></span>.',11,5,34);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8062,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch11/Fig11-07_sqWave_FT.png" alt="FILES/SP1Figures/Ch11/Fig11-07_sqWave_FT.png"></center><br><div class="ITS_Caption">Fourier transform of the square wave shown in Fig. <a href="fig:sq_wave_again" class="ITS_reference">&raquo;</a>. The transform <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(j\\omega)"/> has regularly spaced impulses at <span class="ITS_HTML">&omega; = 2&pi; k/T<sub>0</sub></span>.</span></div></div>','11','5','34');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8063,'equation','eq:fourier_series_2','x(t) = \\sum_{k=-\\infty}^{\\infty} a_k e^{jk\\omega_0 t}',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8064,'equation','eq:fourier_coef_2','a_k = \\frac{1}{\\Tfs} \\int_{-\\Tfs/2}^{\\Tfs/2} x(t)e^{-jk\\omega_0 t} dt',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8065,'equation','eq:periodic_fourier_trans','X(j\\omega) =  \\sum_{k=-\\infty}^{\\infty} 2\\pi a_k\\, \\delta(\\omega - k\\omega_0)',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8066,'equation','eq:gen_periodic_pair',' \\begin{array}{ccc} \\BlackTD && \\OrangeFD\\\\ { \\sum_{k=-\\infty}^{\\infty} a_k e^{jk\\omega_0 t}}  & \\Fgoes & \\OrangeFT  { \\sum_{k=-\\infty}^{\\infty} 2\\pi a_k \\delta(\\omega - k\\omega_0)}   \\\\ \\end{array} ',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8067,'math',NULL,'x(t)=x(t+\\Tfs)',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8068,'math',NULL,'t',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8069,'math',NULL,'\\Tfs',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8070,'math',NULL,'\\omega_0=2\\pi/\\Tfs',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8071,'math',NULL,'x(t)',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8072,'math',NULL,'\\omega_0',11,5,35);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8073,'paragraph',NULL,'Now we are ready to obtain a general formula for the Fourier transform of any periodic function for which a Fourier series exists.  Recall that if <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x(t)=x(t+\\Tfs)"/> for all <span class="ITS_HTML">t</span>, then the signal is periodic with period <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\Tfs"/> and it can be represented by the sum of complex exponentials <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?x(t) = \\sum_{k=-\\infty}^{\\infty} a_k e^{jk\\omega_0 t}"/></div> where <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\omega_0=2\\pi/\\Tfs"/> and <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?a_k = \\frac{1}{\\Tfs} \\int_{-\\Tfs/2}^{\\Tfs/2} x(t)e^{-jk\\omega_0 t} dt"/></div> Since the Fourier transform of a sum is the sum of corresponding Fourier transforms, and we know the Fourier transforms of the exponential signals from <a href="eq:complex_exp_pair" class="ITS_reference">&raquo;</a>, it follows that the Fourier transform of <span class="ITS_HTML">x(t)</span> in <a href="eq:fourier_series_2" class="ITS_reference">&raquo;</a> is <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?X(j\\omega) =  \\sum_{k=-\\infty}^{\\infty} 2\\pi a_k\\, \\delta(\\omega - k\\omega_0)"/></div> Thus, any periodic signal with fundamental frequency <span class="ITS_HTML">&omega;<sub>0</sub></span> is represented by the following Fourier transform pair as illustrated in Fig. <a href="fig:sq_wave_trans" class="ITS_reference">&raquo;</a> for the square wave in Fig. <a href="fig:sq_wave_again" class="ITS_reference">&raquo;</a>. <div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi? \\begin{array}{ccc} \\BlackTD && \\OrangeFD\\\\ { \\sum_{k=-\\infty}^{\\infty} a_k e^{jk\\omega_0 t}}  & \\Fgoes & \\OrangeFT  { \\sum_{k=-\\infty}^{\\infty} 2\\pi a_k \\delta(\\omega - k\\omega_0)}   \\\\ \\end{array} "/></div>','11','5','35');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8074,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Substitute <a href="eq:periodic_fourier_trans" class="ITS_reference">&raquo;</a> into the inverse Fourier transform integral in <a href="eq:inv_fourier_trans" class="ITS_reference">&raquo;</a> and show that when evaluated using the properties of the impulse function, the result is identical to <a href="eq:fourier_series_2" class="ITS_reference">&raquo;</a>. </div>','11','5','37');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8075,'equation','eq:fourier_coef_sq_wave','\\begin{align} a_k &=  \\frac{1}{\\Tfs} \\int_{-\\Tph}^{\\Tph} e^{-jk\\omega_0 t} dt \\nonumber \\\\ & = \\left. \\frac{1}{\\Tfs}\\frac{e^{-jk\\omega_0 t}}{(-jk\\omega_0)} \\right|_{-\\Tph}^{\\Tph} =  \\frac{\\sin(k\\omega_0 \\Tph)}{k\\omega_0 \\Tfs/2}  \\end{align}',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8076,'equation','eq:DC_coefficient','a_0=\\frac{1}{\\Tfs} \\int_{-\\Tph}^{\\Tph} dt = \\frac{T}{T_0}= \\frac{1}{2}',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8077,'equation','eq:fourier_coef_sq_wave-2','a_k = \\begin{cases} { \\frac{\\sin(\\pi k/2)}{\\pi k}} & \\quad  k\\neq0\\\\ \\frac{1}{2} & \\quad  k=0 \\end{cases}',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8078,'equation','eq:fourier_trans_sq_wave','X(j\\omega) = \\pi \\delta(\\omega) + \\sum_{\\stackrel{\\scriptstyle k=-\\infty}{k\\neq 0}}^{\\infty} \\left ( \\frac{2\\sin(\\pi k/2)}{k}\\right ) \\delta(\\omega - k\\omega_0)',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8079,'image','Chapter11/art/sq_wave_trans','Signal $x(t)$ is a 50\% duty cycle square wave whose period is $\Tfs=2\Tp$. Its transform is shown in \protect{Fig. REFERENCE#fig:sq_wave_trans#REFERENCE}.',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8080,'math',NULL,'\\Tfs=2\\Tp',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8081,'math',NULL,'x(t)',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8082,'math',NULL,'\\Tfs=2\\Tp',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8083,'math',NULL,'\\omega_0=2\\pi/\\Tfs',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8084,'math',NULL,'(\\omega_0 \\Tp) = (2\\pi/\\Tfs)(\\Tfs/2) = \\pi',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8085,'math',NULL,'\\Tfs=2\\Tp',11,5,38);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8086,'math',NULL,'\\omega_0',11,5,38);
