INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8143,'math',NULL,'a = -1',11,6,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8144,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: Flip Property</span><p>%  As an example of the use of <a href="eq:scale_pair" class="ITS_reference">&raquo;</a>, suppose that we flip <span class="ITS_HTML">x(t)</span> in time, i.e.,  <span class="ITS_HTML">y(t)=x(-t)</span>. We can show that the resulting Fourier transform is also flipped in frequency. Since <span class="ITS_HTML">y(t)=x(-t)</span>, we use <span class="ITS_HTML">a = -1</span> in <a href="eq:scale_pair" class="ITS_reference">&raquo;</a> to get <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? Y(j\\omega) = \$ \\frac{1{|-1|}$} X(j(\\omega/(-1))) = \\Xmoma "/></div> </div>','11','6','7');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8145,'equation','eq:time_reversal_pair',' \\begin{array}{ccc} \\BlackTD && \\OrangeFD\\\\ x(-t)  &  \\Fgoes &  \\OrangeFT  \\Xmoma  \\\\ \\end{array} ',11,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8146,'paragraph',NULL,'Thus we have derived the flip property of the Fourier transform: <div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi? \\begin{array}{ccc} \\BlackTD && \\OrangeFD\\\\ x(-t)  &  \\Fgoes &  \\OrangeFT  \\Xmoma  \\\\ \\end{array} "/></div>','11','6','8');
