INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8477,'paragraph',NULL,'The multiplication property is very useful for describing a class of systems called amplitude modulators. For this reason, we  also refer to it as the <i> modulation property</i> of Fourier transforms. The basic amplitude modulator system is depicted in Figure <a href="fig:amp_mod" class="ITS_reference">&raquo;</a>. <div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch11/Fig11-15_AMsystem.png" alt="FILES/SP1Figures/Ch11/Fig11-15_AMsystem.png"></center><br><div class="ITS_Caption">Block diagram representation of an amplitude modulator.</span></div></div> \iffalse \begin{figure}[h]  \setlength{\unitlength}{1in} \thicklines \begin{picture}(2.8,0.6)(0.5,0.6) \put(0,1){\vector(1,0){1.3}} \put(1.5,1){\vector(1,0){1.3}} \put(1.4,.6){\vector(0,1){.3}} \put(1.4,1){\circle{.2}} \put(1.33,.93){\line(1,1){.14}} \put(1.47,.93){\line(-1,1){.14}} \put(.8,.45){\dashbox{.05}(1.15,0.8)} \put(0,1.1){\makebox(0,0)[bl]{<span class="ITS_HTML">x(t)</span>}} \put(0,.9){\makebox(0,0)[tl]{<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(j\\omega)"/>}} \put(2.6,1.1){\makebox(0,0)[bl]{<span class="ITS_HTML">y(t)=p(t)x(t)</span>}} \put(2.6,.9){\makebox(0,0)[tl]{<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?Y(j\\omega)=\\frac{1}{2\\pi} X(j\\omega)*P(j\\omega)"/>}} \put(1.5,.6){\makebox(0,0)[bl]{<span class="ITS_HTML">p(t)</span>}} \end{picture}   Block diagram representation of an amplitude modulator. \end{figure} \fi','11','9','4');
