INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8482,'equation','eq:mult_complex_exp','\\begin{align} Y(j\\omega) &= \\frac{1}{2\\pi} X(j\\omega)*2\\pi\\delta(\\omega-\\omega_0)\\\\ &= X(j(\\omega-\\omega_0)) \\end{align}',11,9,7);
