INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8484,'equation','eq:cosine_mult_pair',' \\begin{array}{ccc} \\BlackTD && \\OrangeFD\\\\ x(t)\\cos(\\omega_0 t) & \\Fgoes &\\\\ \\multicolumn{3}{l}{\\quad  \\quad     \\OrangeFT \\frac{1}{2} X(j(\\omega-\\omega_0)) + \\frac{1}{2} X(j(\\omega+\\omega_0)) }  \\\\ \\end{array} ',11,9,7);
