INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8500,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch11/Fig11-16_pulse_mod_FT.png" alt="FILES/SP1Figures/Ch11/Fig11-16_pulse_mod_FT.png"></center><br><div class="ITS_Caption">Fourier transform of pulse modulated cosine wave: (a) Time function <span class="ITS_HTML">y(t)=x(t)cos(&omega;<sub>0</sub> t)</span>; (b) Corresponding Fourier transform <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?Y(j\\omega) = \\frac{1}{2} X(j(\\omega-\\omega_0)) + \\frac{1}{2} X(j(\\omega+\\omega_0))"/>.</span></div></div>','11','9','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8501,'equation','none',' X(j\\omega) = \\frac{\\sin(\\omega \\Tph)}{(\\omega/2)} ',11,9,9);
