INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8509,'equation','none','\\begin{eqnarray*} Y(j\\omega) & = & \\frac{1}{2} X(j(\\omega-\\omega_0)) + \\frac{1}{2} X(j(\\omega+\\omega_0))\\\\ & = &\\frac{\\sin((\\omega-\\omega_0) \\Tph)}{(\\omega-\\omega_0)} + \\frac{\\sin((\\omega+\\omega_0) \\Tph)}{(\\omega+\\omega_0)} \\end{eqnarray*}',11,9,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8510,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: Finite-Length Sinusoid</span><p>% As an example of the use of <a href="eq:cosine_mult_pair" class="ITS_reference">&raquo;</a>, suppose that <span class="ITS_HTML">x(t)</span> is the rectangular pulse signal <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x(t)=[u(t+\\Tph)-u(t-\\Tph)]"/>, which has Fourier transform <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? X(j\\omega) = \\frac{\\sin(\\omega \\Tph)}{(\\omega/2)} "/></div> Then if we form the signal <span class="ITS_HTML">y(t)= x(t)cos(&omega;<sub>0</sub> t)</span>, by <a href="eq:cosine_mult_pair" class="ITS_reference">&raquo;</a> the corresponding Fourier transform is <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{eqnarray*} Y(j\\omega) & = & \\frac{1}{2} X(j(\\omega-\\omega_0)) + \\frac{1}{2} X(j(\\omega+\\omega_0))\\\\ & = &\\frac{\\sin((\\omega-\\omega_0) \\Tph)}{(\\omega-\\omega_0)} + \\frac{\\sin((\\omega+\\omega_0) \\Tph)}{(\\omega+\\omega_0)} \\end{eqnarray*}"/></div> Figure <a href="fig:pulse_mod" class="ITS_reference">&raquo;</a>(a) shows the product signal <span class="ITS_HTML">y(t)</span> and Fig. <a href="fig:pulse_mod" class="ITS_reference">&raquo;</a>(b) shows the corresponding Fourier transform. Since <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(j\\omega)"/> is approximately bandlimited to low frequencies, the pulse modulated cosine wave has a Fourier transform that is concentrated near <span class="ITS_HTML">&plusmn; &omega;<sub>0</sub></span>.  The locations of the shifted copies of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(j\\omega)"/> are indicated in Fig. <a href="fig:pulse_mod" class="ITS_reference">&raquo;</a>(b). </div>','11','9','9');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8511,'paragraph',NULL,'The modulation property of Fourier transforms is extremely useful for analyzing amplitude modulation systems and for deriving the sampling theorem.  We will study these and other applications in Chapter <a href="chap:filt_mod_samp" class="ITS_reference">&raquo;</a>.','11','9','10');
