INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8515,'math',NULL,'u(t)',11,10,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8516,'paragraph',NULL,'In this chapter, we have derived a number of  useful transform pairs, and we have also derived several important properties of Fourier transforms.  Table <a href="tab:FT_pairs" class="ITS_reference">&raquo;</a> on p. <a href="tab:FT_pairs" class="ITS_reference">&raquo;</a> includes the Fourier transform pairs that we have derived in this section as well as some transform pairs (such as for <span class="ITS_HTML">u(t)</span>) that we did not derive.','11','10','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8517,'table','tab:FT_pairs','[ht]   \renewcommand{\arraystretch}{1.63} \renewcommand{\JGAP}{\rule[-0.142in]{0in}{.3in}} \renewcommand{\MGAP}{\rule[-0.21in]{0in}{0.48in}}  \begin{tabular}{|c|c|} \hline \multicolumn{2}{|c|}{\normalsize\bf\cblue Table of Fourier Transform Pairs}\JGAP \\ \hline \bitblack{Time-Domain: $x(t)$}\JGAP & \bitblu{Frequency-Domain: $\Xoma$ }\\ \hline\hline $e^{-at}u(t) \quad (a>0) $\JGAP & \OrangeFT ${\displaystyle \MGAP\frac{1}{a+j\oma}}$ \\ \hline $e^{bt}u(-t) \quad (b>0) $\JGAP & \OrangeFT ${\displaystyle \MGAP\frac{1}{b-j\oma}}$ \\ \hline $u(t+\hTph)-u(t-\hTph)$ \JGAP & \OrangeFT ${\displaystyle \MGAP\frac{\sin(\oma \Tph)}{\oma/2}}$ \\ \hline ${\displaystyle \MGAP\frac{\sin(\oma_b t)}{\pi t}}$ \JGAP& \OrangeFT $[u(\oma+\oma_b)-u(\oma-\oma_b)]$ \\ \hline $\delta(t)$\JGAP & \OrangeFT 1 \\ \hline $\delta(t-t_d)$\JGAP & \OrangeFT  $e^{-j\oma t_d}$\\ \hline $u(t)$ \JGAP & \OrangeFT ${\displaystyle \pi\delta(\oma)+ \MGAP\frac{1}{j\oma}}$ \\ \hline 1 \JGAP & \OrangeFT $2\pi\delta(\oma)$ \\ \hline $e^{j\oma_0 t}$\JGAP & \OrangeFT $2\pi\delta(\oma-\oma_0)$ \\ \hline $A\cos(\oma_0 t+\phi)$ \JGAP &  \OrangeFT $\pi A e^{j\phi} \delta(\oma-\oma_0) + \pi A e^{-j\phi} \delta(\oma+\oma_0) $ \\ \hline $\cos(\oma_0 t)$  \JGAP &  \OrangeFT ${\displaystyle \pi \delta(\oma-\oma_0) + \pi \delta(\oma+\oma_0)} $ \\ \hline $\sin(\oma_0 t)$  \JGAP &  \OrangeFT ${\displaystyle -j\pi \delta(\oma-\oma_0) + j\pi \delta(\oma+\oma_0)} $ \\ \hline ${\displaystyle \sum_{k=-\infty}^{\infty} a_k e^{jk\oma_0  t}}$ \JGAP & \OrangeFT ${\displaystyle \MGAP\sum_{k=-\infty}^{\infty} 2\pi a_k \delta(\oma - k\oma_0)}$\\ \hline ${\displaystyle \sum_{n=-\infty}^{\infty} \delta(t-nT)}$ \JGAP & \OrangeFT ${\displaystyle \frac{2\pi}{T} \MGAP  \sum_{k=-\infty}^{\infty} \delta(\oma - \frac{2\pi }{T}k)}$\\ \hline \end{tabular}',11,10,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8518,'table','','\\begin{tabular}{|c|c|} \\hline \\multicolumn{2}{|c|}{\\normalsize\\bf Table of Fourier Transform Pairs}\\JGAP \\\\ \\hline \\bitblack{Time-Domain: x(t)}\\JGAP & {Frequency-Domain: X(j\\omega) }\\\\ \\hline\\hline e^{-at}u(t) \\quad  (a>0) \\JGAP & \\OrangeFT { \\MGAP\\frac{1}{a+j\\omega}} \\\\ \\hline e^{bt}u(-t) \\quad  (b>0) \\JGAP & \\OrangeFT { \\MGAP\\frac{1}{b-j\\omega}} \\\\ \\hline u(t+\\hTph)-u(t-\\hTph) \\JGAP & \\OrangeFT { \\MGAP\\frac{\\sin(\\omega \\Tph)}{\\omega/2}} \\\\ \\hline { \\MGAP\\frac{\\sin(\\omega_b t)}{\\pi t}} \\JGAP& \\OrangeFT [u(\\omega+\\omega_b)-u(\\omega-\\omega_b)] \\\\ \\hline \\delta(t)\\JGAP & \\OrangeFT 1 \\\\ \\hline \\delta(t-t_d)\\JGAP & \\OrangeFT  e^{-j\\omega t_d}\\\\ \\hline u(t) \\JGAP & \\OrangeFT { \\pi\\delta(\\omega)+ \\MGAP\\frac{1}{j\\omega}} \\\\ \\hline 1 \\JGAP & \\OrangeFT 2\\pi\\delta(\\omega) \\\\ \\hline e^{j\\omega_0 t}\\JGAP & \\OrangeFT 2\\pi\\delta(\\omega-\\omega_0) \\\\ \\hline A\\cos(\\omega_0 t+\\phi) \\JGAP &  \\OrangeFT \\pi A e^{j\\phi} \\delta(\\omega-\\omega_0) + \\pi A e^{-j\\phi} \\delta(\\omega+\\omega_0)  \\\\ \\hline \\cos(\\omega_0 t)  \\JGAP &  \\OrangeFT { \\pi \\delta(\\omega-\\omega_0) + \\pi \\delta(\\omega+\\omega_0)}  \\\\ \\hline \\sin(\\omega_0 t)  \\JGAP &  \\OrangeFT { -j\\pi \\delta(\\omega-\\omega_0) + j\\pi \\delta(\\omega+\\omega_0)}  \\\\ \\hline { \\sum_{k=-\\infty}^{\\infty} a_k e^{jk\\omega_0  t}} \\JGAP & \\OrangeFT { \\MGAP\\sum_{k=-\\infty}^{\\infty} 2\\pi a_k \\delta(\\omega - k\\omega_0)}\\\\ \\hline { \\sum_{n=-\\infty}^{\\infty} \\delta(t-nT)} \\JGAP & \\OrangeFT { \\frac{2\\pi}{T} \\MGAP  \\sum_{k=-\\infty}^{\\infty} \\delta(\\omega - \\frac{2\\pi }{T}k)}\\\\ \\hline \\end{tabular}',11,10,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8519,'paragraph',NULL,'\newcommand{\JGAP}{} \newcommand{\MGAP}{} <div class="ITS_TABLE"><center>[ht]   \renewcommand{\arraystretch}{1.63} \renewcommand{\JGAP}{\rule[-0.142in]{0in}{.3in}} \renewcommand{\MGAP}{\rule[-0.21in]{0in}{0.48in}}  <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{tabular}{|c|c|} \\hline \\multicolumn{2}{|c|}{\\normalsize\\bf Table of Fourier Transform Pairs}\\JGAP \\\\ \\hline \\bitblack{Time-Domain: x(t)}\\JGAP & {Frequency-Domain: X(j\\omega) }\\\\ \\hline\\hline e^{-at}u(t) \\quad  (a>0) \\JGAP & \\OrangeFT { \\MGAP\\frac{1}{a+j\\omega}} \\\\ \\hline e^{bt}u(-t) \\quad  (b>0) \\JGAP & \\OrangeFT { \\MGAP\\frac{1}{b-j\\omega}} \\\\ \\hline u(t+\\hTph)-u(t-\\hTph) \\JGAP & \\OrangeFT { \\MGAP\\frac{\\sin(\\omega \\Tph)}{\\omega/2}} \\\\ \\hline { \\MGAP\\frac{\\sin(\\omega_b t)}{\\pi t}} \\JGAP& \\OrangeFT [u(\\omega+\\omega_b)-u(\\omega-\\omega_b)] \\\\ \\hline \\delta(t)\\JGAP & \\OrangeFT 1 \\\\ \\hline \\delta(t-t_d)\\JGAP & \\OrangeFT  e^{-j\\omega t_d}\\\\ \\hline u(t) \\JGAP & \\OrangeFT { \\pi\\delta(\\omega)+ \\MGAP\\frac{1}{j\\omega}} \\\\ \\hline 1 \\JGAP & \\OrangeFT 2\\pi\\delta(\\omega) \\\\ \\hline e^{j\\omega_0 t}\\JGAP & \\OrangeFT 2\\pi\\delta(\\omega-\\omega_0) \\\\ \\hline A\\cos(\\omega_0 t+\\phi) \\JGAP &  \\OrangeFT \\pi A e^{j\\phi} \\delta(\\omega-\\omega_0) + \\pi A e^{-j\\phi} \\delta(\\omega+\\omega_0)  \\\\ \\hline \\cos(\\omega_0 t)  \\JGAP &  \\OrangeFT { \\pi \\delta(\\omega-\\omega_0) + \\pi \\delta(\\omega+\\omega_0)}  \\\\ \\hline \\sin(\\omega_0 t)  \\JGAP &  \\OrangeFT { -j\\pi \\delta(\\omega-\\omega_0) + j\\pi \\delta(\\omega+\\omega_0)}  \\\\ \\hline { \\sum_{k=-\\infty}^{\\infty} a_k e^{jk\\omega_0  t}} \\JGAP & \\OrangeFT { \\MGAP\\sum_{k=-\\infty}^{\\infty} 2\\pi a_k \\delta(\\omega - k\\omega_0)}\\\\ \\hline { \\sum_{n=-\\infty}^{\\infty} \\delta(t-nT)} \\JGAP & \\OrangeFT { \\frac{2\\pi}{T} \\MGAP  \\sum_{k=-\\infty}^{\\infty} \\delta(\\omega - \\frac{2\\pi }{T}k)}\\\\ \\hline \\end{tabular}"/></div></center><br><div class="ITS_Caption">Basic Fourier transform pairs.</span></div></div>','11','10','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8520,'table','tab:FT_properties','[ht]   \renewcommand{\arraystretch}{2.0} \renewcommand{\JGAP}{\rule[-0.15in]{0in}{0.2in}} \renewcommand{\MGAP}{\rule[0pt]{0in}{0pt}}  \setlength{\unitlength}{1in} \begin{tabular}{|l|c|c|} \hline \multicolumn{3}{|c|}{\large\bf\cblue Table of Fourier Transform Properties}\JGAP \\ \hline \bitblack{Property Name} & \bitblack{Time-Domain: $x(t)$}\JGAP & \bitblu{Frequency-Domain: $\Xoma$ }\\ \hline\hline Linearity \JGAP & $ax_1(t)+bx_2(t)$ & \OrangeFT $aX_1(j\oma)+bX_2(j\oma)$ \\ \hline Conjugation \JGAP& $x^*(t)$ &\OrangeFT  $X^*(-j\oma)$ \\ \hline Time-Reversal\JGAP & $x(-t)$ & \OrangeFT  $\Xmoma$ \\ \hline Scaling \JGAP& $x(at)$ & \OrangeFT $\frac{1}{|a|} X(j(\oma/a))$ \\ \hline Delay \JGAP & $x(t-t_d)$ & \OrangeFT  ${\displaystyle e^{-j\oma t_d} \Xoma} $ \\ \hline Modulation \JGAP & $x(t)e^{j\oma_0 t} $ & \OrangeFT $X(j(\oma-\oma_0))$ \\ \hline Modulation \JGAP& $x(t)\cos(\oma_0 t)$ & \OrangeFT $\frac{1}{2} X(j(\oma-\oma_0))+\frac{1}{2} X(j(\oma+\oma_0))$ \\ \hline Differentiation \JGAP & ${\displaystyle \frac{d^k x(t)}{dt^k\MGAP}}$ & \OrangeFT $(j\oma)^k \Xoma$ \\ \hline Convolution\JGAP & $x(t)*h(t)$ & \OrangeFT $\Xoma\Homa$ \\ \hline Multiplication\JGAP & $x(t)p(t)$ & \OrangeFT $\frac{1}{2\pi} \Xoma*P(j\oma)$ \\ \hline \end{tabular}',11,10,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8521,'table','','\\begin{tabular}{|l|c|c|} \\hline \\multicolumn{3}{|c|}{\\large\\bf Table of Fourier Transform Properties}\\JGAP \\\\ \\hline \\bitblack{Property Name} & \\bitblack{Time-Domain: x(t)}\\JGAP & {Frequency-Domain: X(j\\omega) }\\\\ \\hline\\hline Linearity \\JGAP & ax_1(t)+bx_2(t) & \\OrangeFT aX_1(j\\omega)+bX_2(j\\omega) \\\\ \\hline Conjugation \\JGAP& x^*(t) &\\OrangeFT  X^*(-j\\omega) \\\\ \\hline Time-Reversal\\JGAP & x(-t) & \\OrangeFT  \\Xmoma \\\\ \\hline Scaling \\JGAP& x(at) & \\OrangeFT \\frac{1}{|a|} X(j(\\omega/a)) \\\\ \\hline Delay \\JGAP & x(t-t_d) & \\OrangeFT  { e^{-j\\omega t_d} X(j\\omega)}  \\\\ \\hline Modulation \\JGAP & x(t)e^{j\\omega_0 t}  & \\OrangeFT X(j(\\omega-\\omega_0)) \\\\ \\hline Modulation \\JGAP& x(t)\\cos(\\omega_0 t) & \\OrangeFT \\frac{1}{2} X(j(\\omega-\\omega_0))+\\frac{1}{2} X(j(\\omega+\\omega_0)) \\\\ \\hline Differentiation \\JGAP & { \\frac{d^k x(t)}{dt^k\\MGAP}} & \\OrangeFT (j\\omega)^k X(j\\omega) \\\\ \\hline Convolution\\JGAP & x(t)*h(t) & \\OrangeFT X(j\\omega)H(j\\omega) \\\\ \\hline Multiplication\\JGAP & x(t)p(t) & \\OrangeFT \\frac{1}{2\\pi} X(j\\omega)*P(j\\omega) \\\\ \\hline \\end{tabular}',11,10,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8522,'paragraph',NULL,'The basic properties of the Fourier transform are what make it convenient to use in designing and analyzing systems, so they are given in Table <a href="tab:FT_properties" class="ITS_reference">&raquo;</a> on p. <a href="tab:FT_properties" class="ITS_reference">&raquo;</a> for easy reference.% <div class="ITS_TABLE"><center>[ht]   \renewcommand{\arraystretch}{2.0} \renewcommand{\JGAP}{\rule[-0.15in]{0in}{0.2in}} \renewcommand{\MGAP}{\rule[0pt]{0in}{0pt}}  \setlength{\unitlength}{1in} <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{tabular}{|l|c|c|} \\hline \\multicolumn{3}{|c|}{\\large\\bf Table of Fourier Transform Properties}\\JGAP \\\\ \\hline \\bitblack{Property Name} & \\bitblack{Time-Domain: x(t)}\\JGAP & {Frequency-Domain: X(j\\omega) }\\\\ \\hline\\hline Linearity \\JGAP & ax_1(t)+bx_2(t) & \\OrangeFT aX_1(j\\omega)+bX_2(j\\omega) \\\\ \\hline Conjugation \\JGAP& x^*(t) &\\OrangeFT  X^*(-j\\omega) \\\\ \\hline Time-Reversal\\JGAP & x(-t) & \\OrangeFT  \\Xmoma \\\\ \\hline Scaling \\JGAP& x(at) & \\OrangeFT \\frac{1}{|a|} X(j(\\omega/a)) \\\\ \\hline Delay \\JGAP & x(t-t_d) & \\OrangeFT  { e^{-j\\omega t_d} X(j\\omega)}  \\\\ \\hline Modulation \\JGAP & x(t)e^{j\\omega_0 t}  & \\OrangeFT X(j(\\omega-\\omega_0)) \\\\ \\hline Modulation \\JGAP& x(t)\\cos(\\omega_0 t) & \\OrangeFT \\frac{1}{2} X(j(\\omega-\\omega_0))+\\frac{1}{2} X(j(\\omega+\\omega_0)) \\\\ \\hline Differentiation \\JGAP & { \\frac{d^k x(t)}{dt^k\\MGAP}} & \\OrangeFT (j\\omega)^k X(j\\omega) \\\\ \\hline Convolution\\JGAP & x(t)*h(t) & \\OrangeFT X(j\\omega)H(j\\omega) \\\\ \\hline Multiplication\\JGAP & x(t)p(t) & \\OrangeFT \\frac{1}{2\\pi} X(j\\omega)*P(j\\omega) \\\\ \\hline \\end{tabular}"/></div></center><br><div class="ITS_Caption">Basic Fourier transform properties.</span></div></div>','11','10','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8523,'section',NULL,'Using the Fourier Transform for Multipath Analysis',11,11,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8524,'section',NULL,'Using the Fourier Transform for Multipath Analysis',11,11,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8525,'paragraph',NULL,'<h3 class="ITS_BOOK">11-10  Using the Fourier Transform for Multipath Analysis</h3>','11','11','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8526,'equation','eq:echo_model','y(t)=x(t)+\\alpha x(t-t_d)',11,11,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8527,'math',NULL,'\\alpha',11,11,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8528,'math',NULL,'t_d',11,11,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8529,'paragraph',NULL,'In Section <a href="sec:multi_path_time_domain" class="ITS_reference">&raquo;</a> we presented a time-domain analysis of the problem of multipath transmission of signals.  We modeled the multipath with the simple system <div class="ITS_EQUATION">y(t)=x(t)+&alpha; x(t-t<sub>d</sub>)</div> where <span class="ITS_HTML">&alpha;</span> is the amplitude of the echo and <span class="ITS_HTML">t<sub>d</sub></span>  its location. While we were able to arrive at the correct inverse filter for removing the echo, our approach was not systematic and relied mostly on trial and error.  The Fourier transform provides a much more powerful tool for  analyzing such problems.','11','11','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8530,'equation','eq:echo_analysis','\\begin{align} Y(j\\omega)&= \\left(1+\\alpha  e^{-j\\omega t_d}\\right) X(j\\omega)  \\end{align}',11,11,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8531,'equation','eq:freq_resp_echo','H(j\\omega) =  1+\\alpha e^{-j\\omega t_d}',11,11,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8532,'equation','none',' Y(j\\omega)=X(j\\omega)+\\alpha e^{-j\\omega t_d} X(j\\omega) ',11,11,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8533,'math',NULL,'X(j\\omega)',11,11,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8534,'paragraph',NULL,'If we apply the Fourier transform to both sides of <a href="eq:echo_model" class="ITS_reference">&raquo;</a> and use the delay property, we obtain <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? Y(j\\omega)=X(j\\omega)+\\alpha e^{-j\\omega t_d} X(j\\omega) "/></div> Then we factor out <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(j\\omega)"/> to obtain <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align} Y(j\\omega)&= \\left(1+\\alpha  e^{-j\\omega t_d}\\right) X(j\\omega)  \\end{align}"/></div> Applying the convolution property of the Fourier transform to <a href="eq:echo_analysis" class="ITS_reference">&raquo;</a> leads us to the conclusion that the echo system is LTI and that its frequency response is given by <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?H(j\\omega) =  1+\\alpha e^{-j\\omega t_d}"/></div>','11','11','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8535,'equation','none',' h(t)=\\delta(t)+\\alpha \\delta(t-t_d) ',11,11,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8536,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Using the delay property and the fact that the Fourier transform of an impulse is the constant 1, show that the impulse response of the echo system is <div class="ITS_EQUATION"> h(t)=&delta;(t)+&alpha; &delta;(t-t<sub>d</sub>) </div> Compare your answer to <a href="eq:echo_imp_resp" class="ITS_reference">&raquo;</a> on p. <a href="eq:echo_imp_resp" class="ITS_reference">&raquo;</a> in Chapter <a href="chapt:cont_time_signals" class="ITS_reference">&raquo;</a>. </div>','11','11','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8537,'math',NULL,'H(j\\omega)',11,11,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8538,'math',NULL,'\\omega',11,11,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8539,'math',NULL,'2\\pi/t_d',11,11,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8540,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Show that the frequency response <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(j\\omega)"/>  in <a href="eq:freq_resp_echo" class="ITS_reference">&raquo;</a> is periodic in <span class="ITS_HTML">&omega;</span> with period <span class="ITS_HTML">2&pi;/t<sub>d</sub></span>. </div>','11','11','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8541,'image','Chapter11/art/echo_trans5in','Frequency responses of (a) echo system,  $|\Homa|$, and (b) its inverse, $|H_i(j\oma)|$.',11,11,6);
