INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8566,'math',NULL,'X(j\\omega)',11,11,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8567,'math',NULL,'H_i(j\\omega)',11,11,7);
