INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8776,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> For the signal in <a href="eq:sq_wave_input_trans" class="ITS_reference">&raquo;</a>, determine a cutoff frequency of an ideal lowpass filter such that its output will be a constant <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? y_\ lp(t) = A\\quad    -\\infty\\;<\\;t\\;<\\;\\infty "/></div> What is the constant <span class="ITS_HTML">A</span>? </div>','12','2','37');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8777,'equation','none','\\begin{align*} y_\\mbox{ hp}(t) &= x(t) - y_\\mbox{ lp}(t)\\\\ &=  x(t)-\\left(\\frac{1}{2} +\\frac{2}{\\pi}\\cos(\\omega_0 t) - \\frac{2}{3\\pi}\\cos(3\\omega_0 t)\\right) \\end{align*}',12,2,38);
