INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8845,'math',NULL,'p(t)',12,3,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8846,'paragraph',NULL,'In Section <a href="sec:filtering" class="ITS_reference">&raquo;</a> we have explained linear filtering by exploiting the convolution property of Fourier transforms which states that convolution in the time-domain corresponds to multiplication in the frequency-domain. Now we will turn our attention to the dual result that multiplication in the time-domain corresponds to convolution in the frequency-domain. In the important case where one of the multiplied signals is periodic, the multiplication property implies that the spectrum of the other signal is shifted in frequency.   In Chapter <a href="chap:Fourier_trans" class="ITS_reference">&raquo;</a> we indicated that the frequency shift property would be useful for describing a class of communication systems called amplitude modulators. The time-domain and frequency-domain representations of the general amplitude modulator system are shown again in Fig. <a href="fig:amp_mod_12" class="ITS_reference">&raquo;</a>. In this section, we will consider the important special case when <span class="ITS_HTML">p(t)</span> is a sinusoidal signal. \figurepsAlt{Chapter11/art/AMsystem}{Chapter11/art/AMsystem} {Block diagram representation of a general amplitude modulator.}','12','3','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(8847,'equation','eq:amdsb',' \\begin{array}{c} \\mbox{{ DSBAM Definition}}\\\\ y(t)= x(t)\\cos(\\omega_c t) \\end{array} ',12,3,3);
