INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9057,'math',NULL,'y_1(t)=x_1(t)\\cos(\\omega_{c1}t)',12,3,30);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9058,'math',NULL,'y_2(t)=x_2(t)\\cos(\\omega_{c2}t)',12,3,30);
