INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9082,'section',NULL,' Sampling and Reconstruction',12,4,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9083,'paragraph',NULL,'<h3 class="ITS_BOOK">12-3   Sampling and Reconstruction</h3>','12','4','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9084,'paragraph',NULL,'At this point we have sufficient understanding of the Fourier transform to undertake a thorough discussion of sampling and reconstruction of bandlimited signals.  As in the case of sinusoidal modulation, we will rely heavily on the multiplication, frequency shift, and convolution properties of the continuous-time Fourier transform.','12','4','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9085,'equation','eq:mod_imp_train-1','\\begin{align} x_s(t) &= x(t)p(t)\\nonumber\\\\ & =  x(t) \\sum_{n=-\\infty}^{\\infty} \\delta(t-T_s)   \\\\ & = \\sum_{n=-\\infty}^{\\infty} x(t) \\delta(t-nT_s)\\nonumber \\\\ & = \\sum_{n=-\\infty}^{\\infty}x(nT_s) \\delta(t-nT_s) LABEL#eq:mod_imp_train-2#LABEL \\end{align}',12,4,4);
