INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9173,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Repeat Example <a href="exam:samp_example" class="ITS_reference">&raquo;</a> for the case of <span class="ITS_HTML">&omega;<sub>s</sub>=1.5&pi;</span>. Plot <span class="ITS_HTML">X<sub>s</sub>(j&omega;)</span> as in Fig. <a href="fig:samp_example" class="ITS_reference">&raquo;</a>(b) and give an equation for the signal that is reconstructed by the ideal reconstruction filter for <span class="ITS_HTML">&omega;<sub>s</sub>=1.5&pi;</span>. </div>','12','4','11');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9174,'equation','eq:interp_formula','\\begin{align} x_r(t) &= x_s(t)*h_r(t) \\nonumber \\\\ &=\\left(\\sum_{n=-\\infty}^{\\infty} x(nT_s) \\delta(t-nT_s)\\right)*h_r(t) \\nonumber \\\\ &=  \\sum_{n=-\\infty}^{\\infty} x(nT_s) h_r(t-nT_s)  \\end{align}',12,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9175,'equation','eq:ideal_reconst_imp_resp','h_r(t) = \\frac{\\sin\\frac{\\pi}{T_s}t}{\\frac{\\pi}{T_s}t}',12,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9176,'equation','eq:bl_interp_formula',' \\begin{array}{c} \\mbox{{  A bandlimited signal x(t) }}\\\\ \\mbox{{ such that X(j\\omega) =0 for |\\omega|\\geq \\omega_b}}\\\\ \\mbox{{ can be reconstructed exactly from}}\\\\ \\mbox{{ samples taken with sampling}}\\\\ \\mbox{{  rate \\omega_s=2\\pi/T_s \\geq 2\\omega_b via }} \\\\ \\mbox{{ the bandlimited interpolation formula:}}\\\\ {  x_r(t) =\\sum_{n=-\\infty}^{\\infty} x(nT_s)\\frac{\\sin\\left[\\frac{\\pi}{T_s}\\left(t-nT_s\\right)\\right]} {\\frac{\\pi}{T_s}\\left(t-nT_s\\right)}} \\end{array} ',12,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9177,'image','Chapter12/art/reconst_filter','Ideal bandlimited reconstruction filter: (a) Impulse response $h_r(t)$; (b) Frequency response $H_r(j\oma)$.',12,4,12);
