INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9318,'image','Chapter12/art/dt_filtering5in} ','Illustration of discrete-time filtering of a sampled bandlimited signal: (a) Fourier transform of continuous-time bandlimited signal <span class="ITS_HTML">x<sub>c</sub>(t)</span>; (b) Discrete-time Fourier transform (DTFT) of output signal from ideal C-to-D converter plotted as a function of <span class="ITS_HTML">&omega;</span>; (c) DTFT of output signal from ideal C-to-D converter plotted as a function of normalized frequency <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>; (d)  frequency response of ideal lowpass digital filter (plotted vs. <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>); (e) DTFT of output signal after digital lowpass filter and frequency response of ideal reconstruction filter (plotted vs. <span class="ITS_HTML">&omega;</span>); (f) Fourier transform of output of D-to-C converter.',12,4,28);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9319,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch12/Fig12-21_DTFT_filtering.png" alt="FILES/SP1Figures/Ch12/Fig12-21_DTFT_filtering.png"></center><br><div class="ITS_Caption">Illustration of discrete-time filtering of a sampled bandlimited signal: (a) Fourier transform of continuous-time bandlimited signal <span class="ITS_HTML">x<sub>c</sub>(t)</span>; (b) Discrete-time Fourier transform (DTFT) of output signal from ideal C-to-D converter plotted as a function of <span class="ITS_HTML">&omega;</span>; (c) DTFT of output signal from ideal C-to-D converter plotted as a function of normalized frequency <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>; (d)  frequency response of ideal lowpass digital filter (plotted vs. <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>); (e) DTFT of output signal after digital lowpass filter and frequency response of ideal reconstruction filter (plotted vs. <span class="ITS_HTML">&omega;</span>); (f) Fourier transform of output of D-to-C converter.</span></div></div> \iffalse \bfig[h] {\includegraphics[width=.8\hsize]{Chapter12/art/DTFT_example}} Illustration of DTFT of a sampled bandlimited signal: (a) Fourier transform of continuous-time bandlimited signal <span class="ITS_HTML">x<sub>c</sub>(t)</span>; (b) Discrete-time Fourier transform of output of ideal C-to-D converter plotted as a function of <span class="ITS_HTML">&omega;</span>; and (c) Discrete-time Fourier transform of output of ideal C-to-D converter plotted as a function of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\om"/> \efig \fi','12','4','28');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9320,'equation','eq:dt_ideal_lp','H(e^{j\\om}) = \\begin{cases} 1 & \\quad  |\\om | \\leq \\om_\\mbox{ co} \\\\ 0 & \\quad   \\om_\\mbox{ co}<|\\om | < \\pi \\end{cases}',12,4,29);
