INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9355,'math',NULL,'y[n]=x[n-10]',12,4,32);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9356,'math',NULL,'t_d=10T_s',12,4,32);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9357,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H_\ eff(j\\omega)"/> for Time Delay</span><p>% Let the discrete-time system be a 10-sample delay system defined by <span class="ITS_HTML">y[n]=x[n-10]</span>.  The frequency response of this system is <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? H(e^{j\\om}) = e^{-j\\om 10} "/></div> Therefore, the overall effective frequency response for bandlimited input signals that are sampled above the Nyquist rate is <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? H_\ eff(j\\omega) = e^{-j\\omega 10 T_s} "/></div> which, of course, is the frequency response of an ideal time delay system with delay <span class="ITS_HTML">t<sub>d</sub>=10T<sub>s</sub></span> sec. </div>','12','4','32');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9358,'equation','none',' H(e^{j\\om}) =1-e^{-j\\om} = 2j \\sin(\\om/2)e^{-j\\om/2} ',12,4,33);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9359,'equation','none',' H_\\mbox{ eff}(j\\omega) = 2j \\sin(\\omega T_s/2)e^{-j\\omega T_s/2} ',12,4,33);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9360,'math',NULL,'H_\\mbox{ eff}(j\\omega)',12,4,33);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9361,'math',NULL,'y[n]=x[n]-x[n-1]',12,4,33);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9362,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H_\ eff(j\\omega)"/> for First-Difference</span><p>% Suppose that the discrete-time system is a first-difference system described by <span class="ITS_HTML">y[n]=x[n]-x[n-1]</span>.  The corresponding frequency response is <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? H(e^{j\\om}) =1-e^{-j\\om} = 2j \\sin(\\om/2)e^{-j\\om/2} "/></div> Then if this system is used as the discrete-time system in Fig. <a href="fig:dt_filt_ct_sig" class="ITS_reference">&raquo;</a>, and no aliasing distortion occurs in sampling the bandlimited input signal,  the overall effective frequency response is <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? H_\ eff(j\\omega) = 2j \\sin(\\omega T_s/2)e^{-j\\omega T_s/2} "/></div> </div>','12','4','33');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9363,'equation','none',' H_\\mbox{ eff}(j\\omega) = \\begin{cases} 1 & \\quad  1000\\pi < |\\omega| < 2000\\pi \\\\ 0 & \\quad   \\mbox{otherwise} \\end{cases} ',12,4,34);
