INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9365,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div> Determine the frequency response of the discrete-time filter that, when used in the system of Fig. <a href="fig:dt_filt_ct_sig" class="ITS_reference">&raquo;</a>, will give an overall effective frequency response <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? H_\ eff(j\\omega) = \\begin{cases} 1 & \\quad  1000\\pi < |\\omega| < 2000\\pi \\\\ 0 & \\quad   \otherwise \\end{cases} "/></div> when the sampling rate is <span class="ITS_HTML">&omega;<sub>s</sub> =10000&pi;</span> rad/s. </div>','12','4','34');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9366,'section',NULL,' Sampling and Reconstruction',12,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9367,'section',NULL,' Sampling and Reconstruction',12,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9368,'paragraph',NULL,'<h3 class="ITS_BOOK">12-4   Sampling and Reconstruction</h3>','12','5','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9369,'paragraph',NULL,'We have arrived at a point where we have sufficient understanding of the Fourier transform to use it to increase our understanding of  sampling and reconstruction of bandlimited signals.  As in the case of sinusoidal modulation, we will rely heavily on the multiplication, frequency shift, and convolution properties of the continuous-time Fourier transform.','12','5','2');
