INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9418,'caption','fig:sampled_signal_trans','Illustration of frequency-domain representation of sampling: (a) Fourier transform of signal <span class="ITS_HTML">x(t)</span>; (b)  Fourier transform of sampled signal <span class="ITS_HTML">x<sub>s</sub>(t)=p(t)x(t)</span>.',12,5,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9419,'caption','fig:sampled_signal_trans','Illustration of frequency-domain representation of sampling: (a) Fourier transform of signal <span class="ITS_HTML">x(t)</span>; (b)  Fourier transform of sampled signal <span class="ITS_HTML">x<sub>s</sub>(t)=p(t)x(t)</span>.',12,5,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9420,'paragraph',NULL,'Now since <span class="ITS_HTML">&omega;<sub>s</sub>=2&pi; /T<sub>s</sub></span>, we can state the following important result: <div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi? \\begin{array}{l} \\mbox\\it{ The Fourier transform of a periodic impulse train that is} \\\\ \\mbox\\it{ modulated by a signal x(t) is composed of periodically}\\\\ \\mbox\\it{ repeated copies of X(j\\omega); i.e.,}\\\\ { \\hspace*{.4in} X_s(j\\omega) = \\frac{1}{T_s} \\sum_{k=-\\infty}^{\\infty} X(j(\\omega - 2\\pi k/T_s))},\\\\ \\end{array} "/></div> This result is illustrated in Fig. <a href="fig:sampled_signal_trans" class="ITS_reference">&raquo;</a>. \bfig[h] {\includegraphics[scale=0.75]{Chapter13/art/sampled_signal_trans}} Illustration of frequency-domain representation of sampling: (a) Fourier transform of signal <span class="ITS_HTML">x(t)</span>; (b)  Fourier transform of sampled signal <span class="ITS_HTML">x<sub>s</sub>(t)=p(t)x(t)</span>. \efig Figure <a href="fig:sampled_signal_trans" class="ITS_reference">&raquo;</a>(a) shows a typical band-limited Fourier transform representing <span class="ITS_HTML">X(j&omega;)</span>. Figure <a href="fig:sampled_signal_trans" class="ITS_reference">&raquo;</a>(b) shows the periodic replication of  <span class="ITS_HTML">X(j&omega;)</span> according to (<a href="eq:mod_imp_train_trans" class="ITS_reference">&raquo;</a>).  Note that Fig. <a href="fig:sampled_signal_trans" class="ITS_reference">&raquo;</a>(b) was constructed with the assumption that <span class="ITS_HTML">&omega;<sub>s</sub> = 2&pi;/T<sub>s</sub> > 2&omega;<sub>b</sub></span> so the shifted copies of <span class="ITS_HTML">X(j&omega;)</span> do not overlap.  It can be seen that for the example shown in Fig. <a href="fig:sampled_signal_trans" class="ITS_reference">&raquo;</a>(b), we retain a perfect (scaled by <span class="ITS_HTML">1/T<sub>s</sub></span>) copy of <span class="ITS_HTML">X(j&omega;)</span> in the additive combination that comprises the periodic function <span class="ITS_HTML">X<sub>s</sub>(j&omega;)</span>.','12','5','8');
