INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9472,'math',NULL,'T_s',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9473,'math',NULL,'\\omega_\\mbox{ co}=\\pi/T_s',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9474,'math',NULL,'t=0',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9475,'math',NULL,'T_s',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9476,'math',NULL,'h_r(t)',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9477,'math',NULL,'x_s(t)',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9478,'math',NULL,'x_r(t)',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9479,'equation','eq:interp_formula','\\begin{eqnarray} x_r(t) &=& x_s(t)*h_r(t) \\nonumber \\\\ &=&\\left(\\sum_{n=-\\infty}^{\\infty} x(nT_s) \\delta(t-nT_s)\\right)*h_r(t) \\nonumber \\\\ &= & \\sum_{n=-\\infty}^{\\infty} x(nT_s) h_r(t-nT_s). \\end{eqnarray}',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9480,'caption','fig:reconst_filter','Ideal bandlimited reconstruction filter: (a) Impulse response <span class="ITS_HTML">h<sub>r</sub>(t)</span>; (b) Frequency response <span class="ITS_HTML">H<sub>r</sub>(j&omega;)</span>.)',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9481,'caption','fig:reconst_filter','Ideal bandlimited reconstruction filter: (a) Impulse response <span class="ITS_HTML">h<sub>r</sub>(t)</span>; (b) Frequency response <span class="ITS_HTML">H<sub>r</sub>(j&omega;)</span>.)',12,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9482,'paragraph',NULL,'Figure <a href="fig:reconst_filter" class="ITS_reference">&raquo;</a>(a) shows the impulse response  and the frequency response (<a href="eq:reconst_filter_trans" class="ITS_reference">&raquo;</a>) is shown in Fig. <a href="fig:reconst_filter" class="ITS_reference">&raquo;</a>(b). \bfig[ht] {\includegraphics[width=0.9\hsize]{Chapter13/art/reconst_filter}} Ideal bandlimited reconstruction filter: (a) Impulse response <span class="ITS_HTML">h<sub>r</sub>(t)</span>; (b) Frequency response <span class="ITS_HTML">H<sub>r</sub>(j&omega;)</span>.) \efig Note that the ideal lowpass reconstruction filter has gain <span class="ITS_HTML">T<sub>s</sub></span> and cutoff frequency <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\omega_\ co=\\pi/T_s"/>.  The impulse response, which is <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?h_r(t) = \\frac{\\sin\\frac{\\pi}{T_s}t}{\\frac{\\pi}{T_s}t},"/></div> has a maximum value of 1 at <span class="ITS_HTML">t=0</span> and it is zero at integer multiples of <span class="ITS_HTML">T<sub>s</sub></span>. The zeros of <span class="ITS_HTML">h<sub>r</sub>(t)</span> are shown with open circles in Fig. <a href="fig:reconst_filter" class="ITS_reference">&raquo;</a>(a).  The time-domain expression for the output of the reconstruction filter is obtained by substituting (<a href="eq:mod_imp_train-2" class="ITS_reference">&raquo;</a>) for <span class="ITS_HTML">x<sub>s</sub>(t)</span> into <div class="ITS_EQUATION"> x<sub>r</sub>(t)= x<sub>s</sub>(t)*h<sub>r</sub>(t). </div> This substitution leads to <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{eqnarray} x_r(t) &=& x_s(t)*h_r(t) \\nonumber \\\\ &=&\\left(\\sum_{n=-\\infty}^{\\infty} x(nT_s) \\delta(t-nT_s)\\right)*h_r(t) \\nonumber \\\\ &= & \\sum_{n=-\\infty}^{\\infty} x(nT_s) h_r(t-nT_s). \\end{eqnarray}"/></div> That is, as discussed before in Chapter 4, the continuous-time reconstructed signal <span class="ITS_HTML">x<sub>r</sub>(t)</span> consists of an infinite sum of scaled and shifted impulse responses of the reconstruction filter.  When (<a href="eq:ideal_reconst_imp_resp" class="ITS_reference">&raquo;</a>) is substituted into (<a href="eq:interp_formula" class="ITS_reference">&raquo;</a>), we obtain the following result: <div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi? \\begin{array}{l} \\mbox\\it{ A signal x(t) with bandlimited Fourier transform such that X(j\\omega)=0}\\\\ \\mbox\\it{ for |\\omega|\\geq \\omega_b can be reconstructed exactly from samples taken with} \\\\ \\mbox\\it{ sampling rate \\omega_s=2\\pi/T_s \\geq 2\\omega_b using the following bandlimited} \\\\ \\mbox\\it{ interpolation formula:}\\\\ { \\hspace*{.8in} x_r(t) =\\sum_{n=-\\infty}^{\\infty} x(nT_s)\\frac{\\sin\\left[\\frac{\\pi}{T_s}\\left(t-nT_s\\right)\\right]} {\\frac{\\pi}{T_s}\\left(t-nT_s\\right)}}. \\end{array} "/></div>','12','5','12');
