INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9519,'equation','eq:Xs-1','X_s(j\\omega) = \\frac{1}{T_s} \\sum_{k=-\\infty}^{\\infty} X(j(\\omega-2\\pi k/T_s)),',12,5,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9520,'equation','eq:Xs-2','X_s(j\\omega) = \\sum_{n=-\\infty}^{\\infty} x(nT_s) e^{-j\\omega nT_s}=\\sum_{n=-\\infty}^{\\infty} x[n] e^{-j\\omega nT_s},',12,5,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9521,'equation','eq:dtft-1','\\sum_{n=-\\infty}^{\\infty} x[n] e^{-j\\omega nT_s}=\\frac{1}{T_s} \\sum_{k=-\\infty}^{\\infty} X(j(\\omega-2\\pi k/T_s)).',12,5,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9522,'equation','eq:z-transform','X(z) = \\sum_{n=-\\infty}^{\\infty} x[n] z^{-n}.',12,5,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9523,'equation','eq:z-trans_on_unit_circle','\\left.\\sum_{n=-\\infty}^{\\infty} x[n] e^{-j\\omega nT_s}= X(z)\\right|_{ z=e^{j\\omega T_s}}=X(e^{j\\omega T_s}).',12,5,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9524,'equation','eq:freq_norm','\\om =\\omega T_s,',12,5,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9525,'equation','eq:DTFT',' \\begin{array}{c} \\mbox\\it{ The Discrete-Time Fourier Transform} \\\\ { X(e^{j\\om}) =\\left.\\sum_{n=-\\infty}^{\\infty} x[n] e^{-j\\om n}=X(z) \\right|_{ z=e^{j\\om}}}.\\\\ \\end{array} ',12,5,19);
