INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9585,'paragraph',NULL,'\begin{figure}[ht]  \setlength{\unitlength}{.8in} \begin{picture}(5.8,1.6) \put(0,.8){\vector(1,0){.8}} \put(1.8,.8){\vector(1,0){.6}} \put(5,.8){\vector(1,0){.8}} \put(3.4,.8){\vector(1,0){.6}} \put(.8,.4){\framebox(1,.8){\shortstack{C-to-D\\ Converter}}} \put(2.4,.4){\framebox(1,.8){\shortstack{LTI\\ System \\ <span class="ITS_HTML">h[n]</span>, <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\om})"/>}}} \put(4,.4){\framebox(1,.8){\shortstack{D-to-C\\ Converter}}} \put(0,.9){\makebox(0,0)[bl]{<span class="ITS_HTML">x(t)</span>}} \put(2.1,.9){\makebox(0,0)[bc]{<span class="ITS_HTML">x[n]</span>}} \put(3.7,.9){\makebox(0,0)[bc]{<span class="ITS_HTML">y[n]</span>}} \put(5.8,.9){\makebox(0,0)[br]{<span class="ITS_HTML">y(t)</span>}} \put(0,.7){\makebox(0,0)[tl]{<span class="ITS_HTML">X(j&omega;)</span>}} \put(2.1,.7){\makebox(0,0)[tc]{<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(e^{j\\om})"/>}} \put(3.7,.7){\makebox(0,0)[tc]{<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?Y(e^{j\\om})"/>}} \put(5.8,.7){\makebox(0,0)[tr]{<span class="ITS_HTML">Y(j&omega;)</span>}} \put(.6,.2){\dashbox{.05}(4.6,1.2)} \end{picture}  Discrete time filtering of a continuous-time signal. \end{figure}','12','5','23');
