INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9714,'math',NULL,'\\hat{X}_c(j\\omega_k)',13,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9715,'math',NULL,'\\omega_s=2\\pi/T_s',13,2,3);
