INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9718,'math',NULL,'X[k] = (1/T_s) \\hat{X}_c\\left(j\\frac{2\\pi k}{NT_s}\\right)',13,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9719,'math',NULL,'X[k]',13,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9720,'math',NULL,'(1/T_s)X(j\\omega)',13,2,3);
