INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9747,'paragraph',NULL,'<h3 class="ITS_BOOK">13-1.1  Relation of the DTFT to the <span class="ITS_HTML">z</span>-Transform</h3>','13','3','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9748,'equation','eq:DTFT-13',' \\begin{array}{c} \\mbox{{The Discrete-Time Fourier Transform}} \\\\ { X(e^{j\\om}) =\\left.\\sum_{n=-\\infty}^{\\infty} x[n] e^{-j\\om n}=X(z) \\right|_{ z=e^{j\\om}}}\\\\ \\end{array} ',13,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9749,'math',NULL,'x[n]',13,3,6);
