INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9758,'math',NULL,'z',13,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9759,'math',NULL,'\\hat\\omega',13,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9760,'footnote',NULL,'Yet another transform to keep track of!',13,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9761,'footnote',NULL,'Yet another transform to keep track of!',13,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9762,'paragraph',NULL,'First of all, the Fourier transform of a discrete-time signal <span class="ITS_HTML">x[n]</span>, called the <i> discrete-time Fourier transform</i> (DTFT), is defined to be the <span class="ITS_HTML">z</span>-transform<sup><a href="#" class="ITS_footnote">[note]</a></sup> of <span class="ITS_HTML">x[n]</span> evaluated on the unit circle of the <span class="ITS_HTML">z</span>-plane. In other words, the DTFT is <div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi? \\begin{array}{c} \{The Discrete-Time Fourier Transform} \\\\ { X(e^{j\\om}) =\\left.\\sum_{n=-\\infty}^{\\infty} x[n] e^{-j\\om n}=X(z) \\right|_{ z=e^{j\\om}}}\\\\ \\end{array} "/></div> where <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(z)"/> is  the <span class="ITS_HTML">z</span>-transform of <span class="ITS_HTML">x[n]</span>.  Both the <span class="ITS_HTML">z</span>-transform and the DTFT are used to represent discrete-time signals.  The <span class="ITS_HTML">z</span>-transform is a function of a (continuous) complex variable <span class="ITS_HTML">z</span> and the DTFT is a function of a continuous normalized frequency variable <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>.','13','3','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9763,'equation','eq:dtft-14','\\sum_{n=-\\infty}^{\\infty} x[n] e^{-j\\omega n T_s}= \\frac{1}{T_s}\\sum_{\\ell=-\\infty}^{\\infty} X_c(j(\\omega-\\ell\\omega_s))',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9764,'equation','eq:z-trans_on_unit_circle-13','\\sum_{n=-\\infty}^{\\infty} x[n] e^{-j\\omega nT_s} =X(e^{j\\hat\\omega}) \\biggr |_{\\hat\\omega=\\omega T_s}=X(e^{j\\omega nT_s})',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9765,'equation','eq:freq_norm-13','\\om =\\omega T_s',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9766,'equation','eq:DTFT-2-13','X(e^{j\\omega   T_s}) = { \\frac{1}{T_s} \\sum_{\\ell=-\\infty}^{\\infty} X_c(j(\\omega - \\ell\\omega_s))}.',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9767,'math',NULL,'x[n]',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9768,'math',NULL,'x_c(t)',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9769,'math',NULL,'T_s',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9770,'math',NULL,'x[n] = x_c(nT_s)',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9771,'math',NULL,'x[n]',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9772,'math',NULL,'x_c(t)',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9773,'math',NULL,'\\omega_s=2\\pi/T_s',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9774,'math',NULL,'X(e^{j\\hat\\omega})',13,3,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9775,'math',NULL,'x[n]=x_c(nT_s)',13,3,8);
