INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9985,'equation','eq:yk-running-sum','arr y_k[n] = \\sum\\limits_{\\ell=0}^{N-1} x_k[n-\\ell] &=& \\sum\\limits_{m=n-N+1}^n x_k[m] \\nonumber\\\\ &=& \\sum\\limits_{m=n-N+1}^n x[m] e^{-j(2\\pi/N)km}',13,5,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9986,'equation','eq:idft2','x[n] = \\frac{1}{N} \\sum_{\\ell=0}^{N-1} X[\\ell] e^{j(2\\pi /N)\\ell n} \\hbox{ }',13,5,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9987,'equation','eq:yk-is-xk','y_k[n] = X[k] \\hbox{\\ \\  \\ \\ for all $n$} \\hbox{ }',13,5,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(9988,'equation','eq:dft','X[k] = \\sum_{m=0}^{N-1} x[m] e^{-j(2\\pi /N)k m} \\quad   k=0,1,2,\\dots, N{\\,-\\,}1 \\hbox{}',13,5,23);
