INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10155,'math',NULL,'X_c(j(\\omega-\\ell\\omega_s))',13,6,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10156,'math',NULL,'x[n]=x_c(nT_s)',13,6,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10157,'paragraph',NULL,'In the particular case of a bandlimited signal where <span class="ITS_HTML">X<sub>c</sub>(j&omega;)=0</span> for <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?|\\omega|\\geq \\pi/T_s"/>, the shifted copies <span class="ITS_HTML">X<sub>c</sub>(j(&omega;-l&omega;<sub>s</sub>))</span> do not overlap (i.e., no aliasing distortion occurs).  As a result, it is true that <div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi?   X(e^{j\\omega T_s}) = \\frac{1}{T_s}X_c(j\\omega)\\quad  \\text{for } |\\omega| < \\pi/T_s "/></div> This  important result states that the CTFT of a bandlimited signal can be determined  to within a constant multiplier by determining the DTFT of its samples, <span class="ITS_HTML">x[n]=x<sub>c</sub>(nT<sub>s</sub>)</span>. This is the basis for using digital computation in spectrum analysis. Figure <a href="fig:comp_CTFT" class="ITS_reference">&raquo;</a> shows how  the DTFT would be used to compute the spectrum of a continuous-time bandlimited signal.','13','6','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10158,'math',NULL,'x_c(t)',13,6,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10159,'math',NULL,'  X(e^{j\\omega T_s}) = \\frac{1}{T_s}X_c(j\\omega)',13,6,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10160,'paragraph',NULL,'\figurepsAlt{Chapter13/art/comp_CTFT}{Chapter13/art/comp_CTFT} {Computing the Fourier transform of a sampled signal by computing the DTFT. When the input <span class="ITS_HTML">x<sub>c</sub>(t)</span> is bandlimited and the sampling rate is above the Nyquist rate, the DTFT is proportional to the CTFT, i.e., <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?  X(e^{j\\omega T_s}) = \\frac{1}{T_s}X_c(j\\omega)"/>% }','13','6','7');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10161,'equation','eq:DTFT-finite-length','X(e^{j\\hat\\omega}) = \\sum_{n=0}^{L-1} x[n] e^{-j\\hat\\omega n}',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10162,'equation','eq:DFT-finite-length','X(e^{j(2\\pi/N)k}) = \\sum_{n=0}^{L-1} x[n] e^{-j(2\\pi/N)k n}=X[k]',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10163,'math',NULL,'x[n]',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10164,'math',NULL,'x[n]\\neq 0',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10165,'math',NULL,'0\\leq n \\leq \\Lmone',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10166,'math',NULL,'\\hat\\omega_k=(2\\pi/N)k',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10167,'math',NULL,'k=0,1,\\ldots, \\Nmone',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10168,'subsection',NULL,'Relation of the DFT to the DTFT',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10169,'subsection',NULL,'Relation of the DFT to the DTFT',13,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10170,'paragraph',NULL,'<h3 class="ITS_BOOK">13-4.3  Relation of the DFT to the DTFT</h3>  The DFT is sampled in frequency, so how does the DFT fit into this picture? If we only consider finite-length sequences <span class="ITS_HTML">x[n]</span> such that <span class="ITS_HTML">x[n]&ne; 0</span> within the interval <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?0\\leq n \\leq \\Lmone"/>, then the DTFT is <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?X(e^{j\\hat\\omega}) = \\sum_{n=0}^{L-1} x[n] e^{-j\\hat\\omega n}"/></div> The DFT is obtained from the DTFT  by evaluating <a href="eq:DTFT-finite-length" class="ITS_reference">&raquo;</a> at a discrete set of equally spaced frequencies  <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega_k=(2\\pi/N)k"/>, for <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?k=0,1,\\ldots, \\Nmone"/> <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?X(e^{j(2\\pi/N)k}) = \\sum_{n=0}^{L-1} x[n] e^{-j(2\\pi/N)k n}=X[k]"/></div>','13','6','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10171,'equation','eq:DFT-CTFT','\\begin{align} X[k] &= X(e^{j(2\\pi/N)k}) = \\left. X(e^{j\\omega T_s})\\right|_{\\omega =\\frac{2\\pi k}{NT_s}} \\nonumber \\\\ &= \\left. { \\frac{1}{T_s} \\sum_{\\ell=-\\infty}^{\\infty} X_c\\left(j(\\omega - \\ell\\frac{2\\pi}{T_s})\\right)}\\right|_{\\omega =\\frac{2\\pi k}{NT_s}}  \\end{align}',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10172,'equation','eq:freq_k_to_omega','k  = \\left( \\frac{N T_s}{2\\pi} \\right) \\omega_k',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10173,'math',NULL,'\\bullet',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10174,'math',NULL,'k',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10175,'math',NULL,'\\omega_k',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10176,'math',NULL,'\\bullet',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10177,'math',NULL,'\\bullet',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10178,'subsection',NULL,'Relation of the DFT to the CTFT',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10179,'subsection',NULL,'Relation of the DFT to the CTFT',13,6,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10180,'paragraph',NULL,'<h3 class="ITS_BOOK">13-4.5  Relation of the DFT to the CTFT</h3>  To make the connection between the DFT and the CTFT, we combine <a href="eq:DTFT-2-13" class="ITS_reference">&raquo;</a> and <a href="eq:DFT-finite-length" class="ITS_reference">&raquo;</a>  to obtain <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align} X[k] &= X(e^{j(2\\pi/N)k}) = \\left. X(e^{j\\omega T_s})\\right|_{\\omega =\\frac{2\\pi k}{NT_s}} \\nonumber \\\\ &= \\left. { \\frac{1}{T_s} \\sum_{\\ell=-\\infty}^{\\infty} X_c\\left(j(\\omega - \\ell\\frac{2\\pi}{T_s})\\right)}\\right|_{\\omega =\\frac{2\\pi k}{NT_s}}  \\end{align}"/></div> The mathematical relationship <a href="eq:DFT-CTFT" class="ITS_reference">&raquo;</a> between the DFT of a finite segment of a sequence of samples and the CTFT of the corresponding continuous-time signal involves three issues: \begin{description} <li>{<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\bullet"/>} The DFT is a function of the frequency index <span class="ITS_HTML">k</span> which can be related to  a CTFT  frequency, <span class="ITS_HTML">&omega;<sub>k</sub></span>,  via <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?k  = \\left( \\frac{N T_s}{2\\pi} \\right) \\omega_k"/></div> <li>{<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\bullet"/>} The DFT uses only a finite-length segment of the sampled signal. <li>{<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\bullet"/>} The DFT evaluates the spectrum at only a discrete set of frequencies. \end{description} In the next section, we will address the finite-length issue, because it has the biggest impact on the appearance of the spectrum.','13','6','10');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10181,'math',NULL,'x_c(t)',13,7,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10182,'section',NULL,'An Example of Computing the Fourier Transform',13,7,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10183,'section',NULL,'An Example of Computing the Fourier Transform',13,7,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10184,'image','Chapter13/art/comp_CTFT','Illustration of determining the DTFT of a sampled signal. When the input is bandlimited and the sampling rate is above the Nyquist rate, the DTFT is proportional to the CTFT of <span class="ITS_HTML">x<sub>c</sub>(t)</span> as stated in REFERENCE#eq:DTFT=CTFT#REFERENCE.',13,7,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10185,'paragraph',NULL,'<h3 class="ITS_BOOK">13-6  An Example of Computing the Fourier Transform</h3>  Equation <a href="eq:DTFT=CTFT" class="ITS_reference">&raquo;</a>  shows that the Fourier transform of a bandlimited continuous-time signal can be obtained by first sampling the signal at the Nyquist rate or above, and then determining the DTFT of the resulting sequence of samples. This is depicted in Fig. <a href="fig:comp_CTFT" class="ITS_reference">&raquo;</a>. <div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch13/Fig13-08_comp_CTFT.png" alt="FILES/SP1Figures/Ch13/Fig13-08_comp_CTFT.png"></center><br><div class="ITS_Caption">Illustration of determining the DTFT of a sampled signal. When the input is bandlimited and the sampling rate is above the Nyquist rate, the DTFT is proportional to the CTFT of <span class="ITS_HTML">x<sub>c</sub>(t)</span> as stated in <a href="eq:DTFT=CTFT" class="ITS_reference">&raquo;</a>.</span></div></div>','13','7','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10186,'math',NULL,'x[n]',13,7,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10187,'math',NULL,'X_c(j\\omega)',13,7,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10188,'math',NULL,'|\\omega|\\leq \\pi/T_s',13,7,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10189,'paragraph',NULL,'In a few cases, when the DTFT of the sequence <span class="ITS_HTML">x[n]</span> is known or can easily be determined, we can in principle determine <span class="ITS_HTML">X<sub>c</sub>(j&omega;)</span> for <span class="ITS_HTML">|&omega;|&le; &pi;/T<sub>s</sub></span> using only our skill in mathematical analysis and manipulation. Unfortunately, this will not get us very far in most cases, because there are very few signals for which this is can be done.','13','7','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10190,'equation','none','\\begin{align*} X(e^{j\\omega T_s}) &=  \\begin{cases} { \\frac{1}{T_s}}& \\quad  |\\omega|\\leq \\omega_b \\\\ 0 & \\quad  \\omega_b < |\\omega|\\leq \\frac{1}{2}\\omega_s% \\ (= \\pi/T_s). \\end{cases} \\end{align*}',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10191,'equation','eq:sampled_sinc-13','x[n] =x_c(nT_s) =\\frac{\\sin (\\omega_b nT_s )}{\\pi n T_s},',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10192,'equation','eq:DTFT_sinc','X(e^{j\\omega T_s}) = \\sum_{n=-\\infty}^{\\infty} \\left( \\frac{\\sin \\omega_b T_s n}{\\pi T_s n} \\right) e^{-j\\omega T_s n}.',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10193,'equation','none',' x_c(t)= \\frac{\\sin (\\omega_b t)}{\\pi t}, ',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10194,'equation','none',' X_c(j\\omega) = \\begin{cases} 1 & \\quad  |\\omega|\\leq  \\omega_b \\\\ 0 & \\quad  \\mbox{otherwise} \\end{cases} ',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10195,'math',NULL,'T_s',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10196,'math',NULL,'\\omega',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10197,'math',NULL,'X(e^{j\\omega T_s})',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10198,'math',NULL,'x_c(t)',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10199,'math',NULL,'\\omega_b < \\frac{1}{2}\\omega_s',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10200,'math',NULL,'|\\omega|<\\frac{1}{2}\\omega_s',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10201,'math',NULL,'X(e^{j\\omega T_s})',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10202,'math',NULL,'\\omega',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10203,'math',NULL,'\\omega_s = 2\\pi/T_s',13,7,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10204,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: DTFT of sinc Function</span><p>%  One case where we can work out the DTFT exactly is the signal <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? x_c(t)= \\frac{\\sin (\\omega_b t)}{\\pi t}, "/></div> which we know from our study of the CTFT is a common bandlimited signal. If we sample this signal with sampling period <span class="ITS_HTML">T<sub>s</sub></span>, we obtain the sequence <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?x[n] =x_c(nT_s) =\\frac{\\sin (\\omega_b nT_s )}{\\pi n T_s},"/></div> whose DTFT expressed in terms of <span class="ITS_HTML">&omega;</span> is <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?X(e^{j\\omega T_s}) = \\sum_{n=-\\infty}^{\\infty} \\left( \\frac{\\sin \\omega_b T_s n}{\\pi T_s n} \\right) e^{-j\\omega T_s n}."/></div> Try as we might, we will not be able to &ldquo;sum&rdquo; this series directly to obtain a simple formula for <span class="ITS_HTML">X(e<sup>j&omega; T<sub>s</sub></sup>)</span>. However, we <i> already know</i> that the Fourier transform of the continuous-time signal <span class="ITS_HTML">x<sub>c</sub>(t)</span> is <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? X_c(j\\omega) = \\begin{cases} 1 & \\quad  |\\omega|\\leq  \\omega_b \\\\ 0 & \\quad  \otherwise \\end{cases} "/></div> as plotted in Fig. <a href="fig:DTFT_example2" class="ITS_reference">&raquo;</a>(a). Therefore, we can apply <a href="eq:DTFT=CTFT" class="ITS_reference">&raquo;</a> if <span class="ITS_HTML">&omega;<sub>b</sub> < &frac12;&omega;<sub>s</sub></span>, and obtain  the DTFT for <span class="ITS_HTML">|&omega;|<&frac12;&omega;<sub>s</sub></span> <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align*} X(e^{j\\omega T_s}) &=  \\begin{cases} { \\frac{1}{T_s}}& \\quad  |\\omega|\\leq \\omega_b \\\\ 0 & \\quad  \\omega_b < |\\omega|\\leq \\frac{1}{2}\\omega_s% \\ (= \\pi/T_s). \\end{cases} \\end{align*}"/></div> In addition, <span class="ITS_HTML">X(e<sup>j&omega; T<sub>s</sub></sup>)</span> is periodic in <span class="ITS_HTML">&omega;</span> with period <span class="ITS_HTML">&omega;<sub>s</sub> = 2&pi;/T<sub>s</sub></span> so we get the DTFT shown in Fig. <a href="fig:DTFT_example2" class="ITS_reference">&raquo;</a>(b). </div>','13','7','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10205,'math',NULL,'x_c(t)',13,7,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10206,'math',NULL,'\\omega',13,7,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10207,'math',NULL,'\\tilde{X}(e^{j\\omega T_s})',13,7,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10208,'image','{Chapter13/art/DTFT_example2}','Illustration of DTFT of a sampled bandlimited signal: (a) Fourier transform of continuous-time bandlimited signal <span class="ITS_HTML">x<sub>c</sub>(t)</span>; (b) Discrete-time Fourier transform of output of ideal C-to-D converter plotted as a function of <span class="ITS_HTML">&omega;</span>; and (c) The DTFT <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tilde{X',13,7,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10209,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch13/Fig13-09_DTFT_example2.png" alt="FILES/SP1Figures/Ch13/Fig13-09_DTFT_example2.png"></center><br><div class="ITS_Caption">Illustration of DTFT of a sampled bandlimited signal: (a) Fourier transform of continuous-time bandlimited signal <span class="ITS_HTML">x<sub>c</sub>(t)</span>; (b) Discrete-time Fourier transform of output of ideal C-to-D converter plotted as a function of <span class="ITS_HTML">&omega;</span>; and (c) The DTFT <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tilde{X</span></div></div>(e^{j\\omega T_s})"/> of the truncated signal. }','13','7','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10210,'equation','eq:DTFT_sinc_trunc','\\tilde{X}(e^{j\\omega T_s}) = \\sum_{n=-M}^{M}\\left( \\frac{\\sin \\omega T_s n}{\\pi T_s n}\\right) e^{-j\\omega T_s n}.',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10211,'equation','none',' x[n]=\\frac{\\sin \\omega T_s n}{\\pi T_s n}\\quad     -\\infty\\;<\\;n\\;<\\;\\infty ',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10212,'math',NULL,'\\omega',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10213,'math',NULL,'\\omega',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10214,'math',NULL,'\\tilde{X}(e^{j\\omega T_s})',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10215,'math',NULL,'n',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10216,'math',NULL,'\\pm\\infty',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10217,'math',NULL,'n=0',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10218,'math',NULL,'x[n]',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10219,'footnote',NULL,'This would also be true for <i> any</i> perfectly bandlimited signal.',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10220,'footnote',NULL,'In REFERENCE#eq:DTFT_sinc_trunc#REFERENCE  we have truncated the sum symmetrically around <span class="ITS_HTML">n=0</span> because <span class="ITS_HTML">x[n]</span> is a n even sequence. Any convenient interval could be used leading to a different result.',13,7,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10221,'paragraph',NULL,'Example <a href="ex:DTFT-sinc" class="ITS_reference">&raquo;</a> succeeds because we have a simple way to evaluate <a href="eq:DTFT_sinc" class="ITS_reference">&raquo;</a>. However, we are interested in <i> computing</i> values of of the spectrum, so we want to be able to evaluate <a href="eq:DTFT_sinc" class="ITS_reference">&raquo;</a> numerically  for all frequencies of interest. When we &ldquo;plug in&rdquo; values for <span class="ITS_HTML">&omega;</span>\ and add all the terms in the sum <a href="eq:DTFT_sinc" class="ITS_reference">&raquo;</a>, we run into two problems. First of all,  the sequence <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? x[n]=\\frac{\\sin \\omega T_s n}{\\pi T_s n}\\quad     -\\infty\\;<\\;n\\;<\\;\\infty "/></div> is infinitely long and hence, the sum in <a href="eq:DTFT_sinc" class="ITS_reference">&raquo;</a>  is infinite and cannot be computed numerically even for a single fixed value of <span class="ITS_HTML">&omega;</span>.<sup><a href="#" class="ITS_footnote">[note]</a></sup> An obvious way around this problem would be to truncate the sum to include only a finite number of terms as in <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\tilde{X}(e^{j\\omega T_s}) = \\sum_{n=-M}^{M}\\left( \\frac{\\sin \\omega T_s n}{\\pi T_s n}\\right) e^{-j\\omega T_s n}."/></div> The tilde in <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tilde{X}(e^{j\\omega T_s})"/> denotes the fact that this DTFT corresponds to a truncated signal. Since the sinc function dies out as <span class="ITS_HTML">n</span> increases to <span class="ITS_HTML">&plusmn;&infin;</span>, we might assume that the DTFT of the truncated signal <a href="eq:DTFT_sinc_trunc" class="ITS_reference">&raquo;</a> would be close to the theoretically correct result.In <a href="eq:DTFT_sinc_trunc" class="ITS_reference">&raquo;</a>  we have truncated the sum symmetrically around <span class="ITS_HTML">n=0</span> because <span class="ITS_HTML">x[n]</span> is a n even sequence. Any convenient interval could be used leading to a different result.  Unfortunately, it is not so simple.','13','7','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10222,'math',NULL,'M=5',13,7,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10223,'math',NULL,'-3\\omega_s/2 \\leq \\omega \\leq 3\\omega/2',13,7,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10224,'paragraph',NULL,'Figure <a href="fig:DTFT_example2" class="ITS_reference">&raquo;</a>(c) shows the DTFT of the truncated signal for <span class="ITS_HTML">M=5</span>.  In this case, <a href="eq:DTFT_sinc_trunc" class="ITS_reference">&raquo;</a> was evaluated at a very densely spaced set of frequencies spanning the range <span class="ITS_HTML">-3&omega;<sub>s</sub>/2 &le; &omega; &le; 3&omega;/2</span>, and the resulting values were connected by straight lines to form the plot which looks  continuous  because the spacing between frequencies is very small. We observe that the DTFT of the truncated sinc function resembles the ideal result, but it has some obvious differences.  To understand why, we will need to examine the basic reciprocal relation between time duration and frequency width that we previously studied in Section <a href="sec:scaling-property" class="ITS_reference">&raquo;</a>.','13','7','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10225,'math',NULL,'\\omega_s=2\\pi/T_s',13,7,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10226,'paragraph',NULL,'A second issue is posed by the question, &ldquo;How do we know in advance which frequencies are `of interest&lsquo; to us?&rdquo; To begin, because the DTFT is periodic with period <span class="ITS_HTML">&omega;<sub>s</sub>=2&pi;/T<sub>s</sub></span>, it is only necessary to evaluate <a href="eq:DTFT_sinc_trunc" class="ITS_reference">&raquo;</a>  over one complete period. Then, to make a smooth plot and to be sure that we do not miss any sharp features of the spectrum, we might take the frequencies very close together.  However, the more frequencies we include, the higher the  computational expense, so we generally try to minimize the number of DTFT values computed. In any case, we always have to settle for a <i> finite</i> set of frequencies.','13','7','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10227,'equation','eq:analysis-frequencies','\\omega_k = \\frac{2\\pi k}{NT_s}\\quad   0 \\leq k \\leq N-1.',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10228,'equation','eq:DTFT_sinc_k','\\begin{array}{r} \\tilde{X}(e^{j\\omega_k T_s}) = { \\sum_{n=-M}^{M}\\left( \\frac{\\sin \\omega T_s n}{\\pi T_s n}\\right) e^{-j\\omega_k T_s n}} \\end{array}',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10229,'math',NULL,'N',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10230,'math',NULL,'0 \\leq \\omega < 2\\pi/T_s',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10231,'math',NULL,'0\\leq \\omega < 2\\pi/T_s',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10232,'math',NULL,'\\omega=0',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10233,'math',NULL,'\\omega = 2\\pi/T_s',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10234,'math',NULL,'\\omega_k',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10235,'math',NULL,'k=0,1,2,\\ldots,\\Nmone',13,7,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10236,'paragraph',NULL,'The most common choice for digital spectrum analysis computations is a set of <span class="ITS_HTML">N</span> frequencies equally-spaced within the range <span class="ITS_HTML">0 &le; &omega; < 2&pi;/T<sub>s</sub></span>. Thus, we would evaluate <a href="eq:DTFT_sinc_trunc" class="ITS_reference">&raquo;</a> at frequencies <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\omega_k = \\frac{2\\pi k}{NT_s}\\quad   0 \\leq k \\leq N-1."/></div> This covers the range <span class="ITS_HTML">0&le; &omega; < 2&pi;/T<sub>s</sub></span> without including both <span class="ITS_HTML">&omega;=0</span> and <span class="ITS_HTML">&omega; = 2&pi;/T<sub>s</sub></span> which have identical DTFT values. Thus <a href="eq:DTFT_sinc_trunc" class="ITS_reference">&raquo;</a> becomes <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\begin{array}{r} \\tilde{X}(e^{j\\omega_k T_s}) = { \\sum_{n=-M}^{M}\\left( \\frac{\\sin \\omega T_s n}{\\pi T_s n}\\right) e^{-j\\omega_k T_s n}} \\end{array}"/></div> where  <span class="ITS_HTML">&omega;<sub>k</sub></span>, <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?k=0,1,2,\\ldots,\\Nmone"/> are the analysis frequencies given by <a href="eq:analysis-frequencies" class="ITS_reference">&raquo;</a>.','13','7','9');
