INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10324,'math',NULL,'T',13,9,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10325,'math',NULL,'\\Delta\\omega',13,9,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10326,'math',NULL,'\\omega=0',13,9,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10327,'paragraph',NULL,'The   rectangular window given by <a href="eq:rect-window-analog" class="ITS_reference">&raquo;</a> is plotted in Fig. <a href="fig:rect-window-analog" class="ITS_reference">&raquo;</a>(a).   The magnitude of its Fourier transform <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\bigl |W_R(j\\omega) \\bigr | = \\biggl | \\frac{ \\sin (\\omega T/2)}{(\\omega /2)} \\biggr |"/></div> is plotted in Fig. <a href="fig:rect-window-analog" class="ITS_reference">&raquo;</a>(b). Note that the frequency width is inversely proportional to the duration of <span class="ITS_HTML">w<sub>R</sub>(t)</span>.  A measure of the frequency width of <span class="ITS_HTML">W<sub>R</sub>(j&omega;)</span> around <span class="ITS_HTML">&omega;=0</span> is often taken to be the width of the <font color="blue">mainlobe</font> between the first zero crossings, which is <span class="ITS_HTML">&Delta;&omega; = 4&pi;/T</span> in this case. We can see that as <span class="ITS_HTML">T</span> increases, the time window becomes longer but <span class="ITS_HTML">&Delta;&omega;</span> decreases, so the Fourier transform becomes more concentrated around <span class="ITS_HTML">&omega;=0</span>. Also note that the <span class="ITS_blue">sidelobes</span> have significant amplitude.','13','9','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10328,'image','Chapter13/art/rect_window','(a) Rectangular time window, and (b) its Fourier transform. The part of the plot from $-2\pi/T$ to $2\pi/T$ is the <i> mainlobe,</i> while the <i> sidelobes</i> are evident for $|\oma|>2\pi/T$.',13,9,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10329,'math',NULL,'-2\\pi/T',13,9,5);
