INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10332,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch13/Fig13-13_rect_window_abs_FT.png" alt="FILES/SP1Figures/Ch13/Fig13-13_rect_window_abs_FT.png"></center><br><div class="ITS_Caption">(a) Rectangular time window, and (b) its Fourier transform. The part of the plot from <span class="ITS_HTML">-2&pi;/T</span> to <span class="ITS_HTML">2&pi;/T</span> is the <i> mainlobe,</i> while the <i> sidelobes</i> are evident for <span class="ITS_HTML">|&omega;|>2&pi;/T</span>.</span></div></div>','13','9','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10333,'equation','eq:specific_sinusoid_example','x_c(t)= 0.5 + 0.8\\cos(\\omega_1 t)',13,9,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10334,'math',NULL,'W_R(j\\omega)',13,9,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10335,'math',NULL,'A_0=0.5',13,9,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10336,'math',NULL,'A_1=0.8',13,9,6);
