INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10371,'paragraph',NULL,'For the Hamming window, Fig. <a href="fig:hamming-windowed-cos-FT" class="ITS_reference">&raquo;</a> shows the Fourier transform of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tilde{x_c(t)=w_H(t)x(t)"/>} when <span class="ITS_HTML">x(t)</span> is given by <a href="eq:specific_sinusoid_example" class="ITS_reference">&raquo;</a> with  <span class="ITS_HTML">T</span> and <span class="ITS_HTML">&omega;<sub>1</sub></span> the same as in Fig. <a href="fig:windowed-cos-FT" class="ITS_reference">&raquo;</a>. \figurepsAlt{Chapter13/art/hamming_windowed_cos_FT} {Chapter13/art/hamming_windowed_cos_FT}{Effect of using the Hamming window. } This plot has no confusing ripples caused by sidelobes, but a comparison to Fig <a href="fig:windowed-cos-FT" class="ITS_reference">&raquo;</a>(c) shows that the blurred spectrum lines  are about twice as wide as those for the rectangular window.','13','9','12');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10372,'math',NULL,'\\omega_1=\\mbox{',13,9,13);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10373,'math',NULL,'}\\Delta\\omega=3\\pi/T',13,9,13);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10374,'math',NULL,'W_R(j\\omega)',13,9,13);
