INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10377,'paragraph',NULL,'No matter whether we use a rectangular or a Hamming window, if too much overlap occurs, we will no longer see distinct peaks around each of the original frequencies.  This is illustrated in Fig. <a href="fig:windowed-cos-FT2" class="ITS_reference">&raquo;</a> where the three individual peaks  merge into one wide spectral peak in Fig. <a href="fig:windowed-cos-FT2" class="ITS_reference">&raquo;</a>(b). In this case, <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\omega_1=\"/>\frac{3{4<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?}\\Delta\\omega=3\\pi/T"/>} and the overlapping copies of <span class="ITS_HTML">W<sub>R</sub>(j&omega;)</span> fill in the entire region around <span class="ITS_HTML">&omega;=0</span>. \figurepsAlt{Chapter13/art/windowed_cos_FT2} {Chapter13/art/windowed_cos_FT2_mag}{Spectrum of sinusoidal signal for short time window. (a) Fourier transform magnitudes of individual windowed complex-exponential terms and (b) Fourier transform magnitude of windowed cosine signal. (Scale enlarged compared to Fig. <a href="fig:windowed-cos-FT" class="ITS_reference">&raquo;</a>.) } As a result, the complex-exponential components at <span class="ITS_HTML">&plusmn;&omega;<sub>1</sub></span> are not <i> resolved</i> from the DC component.','13','9','13');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10378,'math',NULL,'T',13,9,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10379,'math',NULL,'\\Delta\\omega',13,9,14);
