INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10381,'math',NULL,'2.41\\pi/T',13,9,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10382,'math',NULL,'|W_R(j\\omega)|',13,9,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10383,'math',NULL,'3.63\\pi/T',13,9,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10384,'math',NULL,'|W_H(j\\omega)|',13,9,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10385,'footnote',NULL,'The mainlobe width can be measured in different ways. For example, if it were defined as the frequency width at the half-amplitude level, the result would be <span class="ITS_HTML">2.41&pi;/T</span> for <span class="ITS_HTML">|W<sub>R</sub>(j&omega;)|</span> and <span class="ITS_HTML">3.63&pi;/T</span> for <span class="ITS_HTML">|W<sub>H</sub>(j&omega;)|</span>, which implies that the Hamming window&lsquo;s duration must be 1.5 times that of the rectangular window to obtain  comparable  frequency resolution.',13,9,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10386,'footnote',NULL,'The mainlobe width can be measured in different ways. For example, if it were defined as the frequency width at the half-amplitude level, the result would be <span class="ITS_HTML">2.41&pi;/T</span> for <span class="ITS_HTML">|W<sub>R</sub>(j&omega;)|</span> and <span class="ITS_HTML">3.63&pi;/T</span> for <span class="ITS_HTML">|W<sub>H</sub>(j&omega;)|</span>, which implies that the Hamming window&lsquo;s duration must be 1.5 times that of the rectangular window to obtain  comparable  frequency resolution.',13,9,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10387,'paragraph',NULL,'The phenomenon illustrated by Figs. <a href="fig:windowed-cos-FT" class="ITS_reference">&raquo;</a> and <a href="fig:windowed-cos-FT2" class="ITS_reference">&raquo;</a> is often called the <span class="ITS_blue">uncertainty principle</span> of Fourier analysis.  Specifically, in order to resolve all the frequencies in a sum of sinusoidal components, we need to take a  window length <span class="ITS_HTML">T</span> such that mainlobe width <span class="ITS_HTML">&Delta;&omega;</span> is less than the minimum spacing between adjacent sinusoidal frequencies. For the rectangular window, <span class="ITS_HTML">&Delta;&omega; = 4&pi;/T</span>, but if we use a Hamming window, the window length must be roughly twice the length of a rectangular window in order to have the same <i> frequency resolution</i>  power.<sup><a href="#" class="ITS_footnote">[note]</a></sup>','13','9','14');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10388,'paragraph',NULL,'\iffalse %===================================================== Another important observation that follows from Fig. <a href="fig:windowed-cos-FT" class="ITS_reference">&raquo;</a>(c) is that the time-windowed signal is no longer bandlimited in the frequency-domain. Therefore, aliasing will occur when the windowed signal is sampled as in Fig. <a href="fig:analog_window" class="ITS_reference">&raquo;</a>.  Using using a wider Hamming window will cause the Fourier transform to be more tightly concentrated around the frequencies of the original bandlimited sinusoidal signal, and therefore the aliasing effect will be mitigated. \fi %======================================================','13','9','15');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10389,'equation','eq:rect-windowed-DTFT','\\begin{array}{r} \\tilde{X}(e^{j\\omega_k T_s}) = { \\sum_{n=-\\infty}^{\\infty} w_r[n]x[n] e^{-j\\omega_k T_s n}} \\end{array}',13,9,16);
