INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10400,'subsection','sec:DTFT-windowed-sinusoid','DTFT of a Windowed Sinusoid',13,9,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10401,'subsection','sec:DTFT-windowed-sinusoid','DTFT of a Windowed Sinusoid',13,9,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10402,'paragraph',NULL,'<h3 class="ITS_BOOK">13-7.1  DTFT of a Windowed Sinusoid</h3> Our main objective is to have a method for computing the spectrum, so we must use a system such as Fig. <a href="fig:digital_window" class="ITS_reference">&raquo;</a>, where the windowing is applied <i>after</i> the C-to-D converter. The actual calculation will be frequency samples of the DTFT of the windowed discrete-time sequence <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\begin{array}{r} \\tilde{X}(e^{j\\omega_k T_s}) = { \\sum_{n=-\\infty}^{\\infty} w_r[n]x[n] e^{-j\\omega_k T_s n}} \\end{array}"/></div> evaluated at <span class="ITS_HTML">N</span> frequencies, <span class="ITS_HTML">&omega;<sub>k</sub> = 2&pi; k /N T<sub>s</sub></span>, <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?k=0,1,\\ldots,\\Nmone"/>.  Normally, the DTFT is a function of the discrete-time frequency <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>, but we have made the substitution <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega = \\omega T_s"/> so that the spectrum <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\tilde{X}(e^{j\\omega T_s})"/> is a function of continuous-time frequency in rad/s. For the specific case of <span class="ITS_HTML">x<sub>c</sub>(t)</span> in <a href="eq:sinusoid_example" class="ITS_reference">&raquo;</a>,  the computed spectrum would be as shown in Fig. <a href="fig:cos-example-samp2" class="ITS_reference">&raquo;</a>. The number of frequency samples is <span class="ITS_HTML">N=32</span>, and the sample values are indicated with dots over the region <span class="ITS_HTML">0&le; &omega;<&omega;<sub>s</sub>=2&pi;/T<sub>s</sub></span>. The smooth curve is the DTFT that would be obtained if <span class="ITS_HTML">N&rarr;&infin;</span>.  The dots are the DFT samples.','13','9','16');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10403,'image','Chapter13/art/cos_example_samp2','DTFT of windowed and sampled sinusoidal signal.  The DTFT magnitude     $|\tilde{X}(e^{j\oma_k T_s})|$ was evaluated at 32 frequencies $\oma_k = 2\pi/(32 T_s)$, which are shown as dots on the smooth curve.',13,9,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10404,'math',NULL,'|\\tilde{X}(e^{j\\omega_k T_s})|',13,9,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10405,'math',NULL,'\\omega_k = 2\\pi/(32 T_s)',13,9,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10406,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch13/Fig13-15_cos_example_samp2.png" alt="FILES/SP1Figures/Ch13/Fig13-15_cos_example_samp2.png"></center><br><div class="ITS_Caption">DTFT of windowed and sampled sinusoidal signal.  The DTFT magnitude     <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?|\\tilde{X}(e^{j\\omega_k T_s})|"/> was evaluated at 32 frequencies <span class="ITS_HTML">&omega;<sub>k</sub> = 2&pi;/(32 T<sub>s</sub>)</span>, which are shown as dots on the smooth curve.</span></div></div>','13','9','18');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10407,'equation','eq:Hw-rect-windowed-sinusoid','\\begin{align} \\tilde X(e^{j\\omega T_s})   &=  A_0 W_r(e^{j\\omega T_s}) + \\frac{1}{2} X_1  W_r(e^{j (\\omega-\\omega_1)T_s}) \\\\ &  \\quad  \\quad   +\\frac{1}{2} X_1^* W_r(e^{j (\\omega+\\omega_1)T_s}) \\end{align}',13,9,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10408,'equation','none',' X(e^{j\\omega T_s}) = \\frac{1}{T_s} \\sum_{\\ell=-\\infty}^{\\infty} X_c(j(\\omega-\\ell\\omega_s)) ',13,9,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10409,'math',NULL,'\\omega',13,9,19);
