INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10471,'math',NULL,'0\\leq n \\leq \\Nmone',13,10,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10472,'paragraph',NULL,'If we substitute <a href="eq:DFT-orthogonality" class="ITS_reference">&raquo;</a>  into the third line of <a href="eq:IDFT-derivation" class="ITS_reference">&raquo;</a>  we see that the only term in the sum on <span class="ITS_HTML">m</span> that will be nonzero is the term corresponding to <span class="ITS_HTML">m=n</span> where <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?0\\leq n \\leq \\Nmone"/>. This means that <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?v[n]=\\tilde{x[n]"/>} for <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?0\\leq n \\leq \\Nmone"/> as we wished to show.','13','10','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(10473,'equation','eq:dft3','\\begin{align} \\begin{align} X[k] = \\sum_{n=0}^{N-1} &x[n] e^{-j(2\\pi /N)k n}\\\\ &  \\quad    k=0,1,\\dots, \\Nmone \\end{align} \\\\ \\begin{align} x[n] = \\frac{1}{N} \\sum_{k=0}^{N-1} &X[k] e^{j(2\\pi/N)kn} \\\\ &  \\quad   n=0,1,\\dots, \\Nmone \\end{align} LABEL#eq:idft3#LABEL \\end{align}',13,10,7);
